import { CtaSectionV1 } from "../cta-section-v1"

export default function Page() {
    return <CtaSectionV1 />
}

/* CSS Variables for CTA Section V1 */
/* These variables are required for the component styles to work correctly */
/* Import this file in your globals.css or app layout */

/* Fonts — inherits --font-sans from next/font (globals.css) */

/* ============================================
   CTA Section V1 — Clean black & white
   editorial CTA with display serif headline
   and atmospheric background glow
   ============================================ */

/* ── Section Root ──────────────────────────── */

.cta1-section {
  /* Fonts — uses next/font CSS variables for zero-layout-shift loading */
  --cta1-display: var(--font-noto-serif-display), serif;
  --cta1-sans: var(--font-sans, 'Geist', sans-serif);

  /* Colors — black & white */
  --cta1-accent: var(--foreground);
  --cta1-accent-glow: oklch(0 0 0 / 0.12);

  position: relative;
  width: 100%;
  overflow: hidden;
  container-type: inline-size;
  container-name: cta1;
  color: var(--foreground);
}

.dark .cta1-section {
  --cta1-accent: var(--foreground);
  --cta1-accent-glow: oklch(1 0 0 / 0.12);
}

/* ── Content Glow ────────────────────────────── */

.cta1-glow {
  position: absolute;
  inset: -20% -30%;
  z-index: -1;
  background: radial-gradient(
    50% 50% at 50% 50%,
    var(--cta1-accent-glow) 0%,
    transparent 70%
  );
  pointer-events: none;
}

/* ── Noise Grain Overlay ─────────────────────── */

.cta1-grain {
  position: absolute;
  inset: 0;
  z-index: 1;
  pointer-events: none;
  opacity: 0.35;
  background: url("data:image/svg+xml,%3Csvg viewBox='0 0 256 256' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='cta1-grain'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.8' numOctaves='4' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23cta1-grain)'/%3E%3C/svg%3E");
  background-size: 180px 180px;
  mix-blend-mode: overlay;
}

.dark .cta1-grain {
  opacity: 0.2;
  mix-blend-mode: soft-light;
}

/* ── Inner Content ───────────────────────────── */

.cta1-inner {
  position: relative;
  z-index: 2;
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  max-width: 48rem;
  margin: 0 auto;
  padding: 5rem 1.25rem;
  gap: 1.5rem;
}

/* ── Headline ────────────────────────────────── */

.cta1-headline {
  font-family: var(--cta1-display);
  font-size: clamp(1.75rem, 5vw, 3rem);
  font-weight: 400;
  font-style: normal;
  line-height: 1.18;
  letter-spacing: -0.01em;
  color: var(--foreground);
  margin: 0;
}

.cta1-headline em {
  font-style: italic;
  color: var(--foreground);
}

/* ── Description ─────────────────────────────── */

.cta1-description {
  font-family: var(--cta1-sans);
  font-size: 0.9375rem;
  line-height: 1.7;
  color: var(--muted-foreground);
  max-width: 34rem;
  margin: 0;
}

/* ── CTA Actions ─────────────────────────────── */

.cta1-actions {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.75rem;
  margin-top: 0.5rem;
}

.cta1-btn-primary {
  font-weight: 500;
  padding-inline: 2rem;
  background: var(--foreground);
  border-color: var(--foreground);
  color: var(--background);
}

.cta1-btn-primary:hover {
  opacity: 0.85;
}

.cta1-btn-secondary {
  font-weight: 500;
  padding-inline: 1.75rem;
}

/* ================================
   Responsive — Tablet (640px+)
   ================================ */

@container cta1 (min-width: 640px) {
  .cta1-inner {
    padding: 6rem 2rem;
    gap: 1.75rem;
  }

  .cta1-actions {
    flex-direction: row;
    gap: 1rem;
  }

  .cta1-description {
    font-size: 1rem;
  }
}

/* ================================
   Responsive — Desktop (960px+)
   ================================ */

@container cta1 (min-width: 960px) {
  .cta1-inner {
    padding: 8rem 2.5rem;
    gap: 2rem;
  }

  .cta1-description {
    font-size: 1.0625rem;
  }
}

/* ================================
   Large Desktop (1200px+)
   ================================ */

@container cta1 (min-width: 1200px) {
  .cta1-inner {
    padding: 10rem 3rem;
  }
}

/* ================================
   Accessibility — Reduced Motion
   ================================ */

@media (prefers-reduced-motion: reduce) {
  .cta1-btn-primary,
  .cta1-btn-secondary {
    transition: none;
  }
}
