import { CtaSectionV2 } from "@/registry/new-york/blocks/cta-section/cta-section-v2"

export default function CtaSectionV2Page() {
    return (
        <div className="flex min-h-[600px] w-full items-center justify-center p-4 md:p-8">
            <CtaSectionV2 />
        </div>
    )
}

"use client"

import { useState, useEffect } from "react"
import { useTheme } from "next-themes"
import "./cta-section-v2.css"
import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"
import { NeuroNoise } from "@paper-design/shaders-react"

/* ─── Theme-aware shader colors ─────────────── */

const shaderPalette = {
    light: {
        colorFront: "#d4c8b8",
        colorMid: "#a89888",
        colorBack: "#f0ebe5",
    },
    dark: {
        colorFront: "#4a4035",
        colorMid: "#2e2822",
        colorBack: "#1a1816",
    },
}

export function CtaSectionV2({
    className,
    ...props
}: React.ComponentProps<"section">) {
    const { resolvedTheme } = useTheme()
    const [mounted, setMounted] = useState(false)

    useEffect(() => {
        setMounted(true)
    }, [])

    const isDark = mounted && resolvedTheme === "dark"
    const palette = isDark ? shaderPalette.dark : shaderPalette.light

    return (
        <section
            className={cn("cta2-section", className)}
            {...props}
        >
            <div className="cta2-container">
                <div className="cta2-card">
                    {/* Shader Background */}
                    <div className="cta2-shader-container">
                        {mounted && (
                            <NeuroNoise
                                colorFront={palette.colorFront}
                                colorMid={palette.colorMid}
                                colorBack={palette.colorBack}
                                speed={0.18}
                                brightness={0.15}
                                contrast={0.35}
                                scale={1.2}
                                className="cta2-shader"
                            />
                        )}
                    </div>

                    {/* Content */}
                    <div className="cta2-content">
                        <p className="cta2-eyebrow">Praesent Commodo</p>
                        <div className="cta2-text-group">
                            <h2 className="cta2-headline">
                                Vestibulum ante <span>ipsum primis</span>
                            </h2>
                            <p className="cta2-description">
                                Nullam quis risus eget urna mollis ornare vel eu leo.
                                Cras justo odio, dapibus ut facilisis.
                            </p>
                        </div>

                        <div className="cta2-actions">
                            <Button
                                variant="default"
                                size="sm"
                                className="cta2-btn-primary"
                            >
                                Try It Now
                            </Button>
                            <Button
                                variant="ghost"
                                size="sm"
                                className="cta2-btn-secondary"
                            >
                                Learn More
                            </Button>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    )
}

/* CSS Variables for CTA Section V2 */
/* These variables are required for the component styles to work correctly */
/* Import this file in your globals.css or app layout */

/* Fonts — inherits --font-sans from next/font (globals.css) */

/* ============================================
   CTA Section V2 — Minimalist Shader Card
   Modern, clean, and minimalist design
   ============================================ */

/* ── Section Root ──────────────────────────── */

.cta2-section {
  /* Fonts — uses next/font CSS variables for zero-layout-shift loading */
  --cta2-sans: var(--font-sans, 'Geist', sans-serif);
  --cta2-mono: var(--font-mono, 'Geist Mono', monospace);

  /* Colors */
  --cta2-card-bg: oklch(0 0 0 / 0.025);
  --cta2-card-border: oklch(0 0 0 / 0.1);

  position: relative;
  width: 100%;
  overflow: hidden;
  container-type: inline-size;
  container-name: cta2;
  color: var(--foreground);
}

.dark .cta2-section {
  --cta2-card-bg: oklch(1 0 0 / 0.03);
  --cta2-card-border: oklch(1 0 0 / 0.1);
}

/* ── Container ──────────────────────────────── */

.cta2-container {
  max-width: 72rem;
  margin: 0 auto;
  padding: 6rem 1.5rem;
}

/* ── Card ────────────────────────────────────── */

.cta2-card {
  position: relative;
  width: 100%;
  min-height: 400px;
  border-radius: 2rem;
  background: var(--cta2-card-bg);
  border: 1px solid var(--cta2-card-border);
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

/* ── Shader Background ───────────────────────── */

.cta2-shader-container {
  position: absolute;
  inset: 0;
  z-index: 0;
  opacity: 0.7;
  pointer-events: none;
  mix-blend-mode: multiply;
}

.dark .cta2-shader-container {
  opacity: 0.6;
  mix-blend-mode: lighten;
}

.cta2-shader {
  width: 100%;
  height: 100%;
}

/* ── Content ─────────────────────────────────── */

.cta2-content {
  position: relative;
  z-index: 1;
  padding: 4rem 2rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1.75rem;
  max-width: 42rem;
  backface-visibility: hidden;
}

.cta2-eyebrow {
  font-family: var(--cta2-mono);
  font-size: 0.6875rem;
  font-weight: 500;
  letter-spacing: 0.15em;
  text-transform: uppercase;
  color: var(--muted-foreground);
  margin: 0;
  opacity: 0.7;
}

.cta2-text-group {
  display: flex;
  flex-direction: column;
  gap: 0.875rem;
}

.cta2-headline {
  font-family: var(--cta2-sans);
  font-size: clamp(2rem, 5.5vw, 3.25rem);
  font-weight: 500;
  line-height: 1.08;
  letter-spacing: -0.04em;
  color: var(--foreground);
  margin: 0;
  text-wrap: balance;
}

.cta2-headline span {
  color: var(--muted-foreground);
  font-weight: 300;
  font-style: italic;
}

/* ── Description ─────────────────────────────── */

.cta2-description {
  font-family: var(--cta2-sans);
  font-size: 0.9375rem;
  line-height: 1.65;
  color: var(--muted-foreground);
  max-width: 28rem;
  margin: 0 auto;
  letter-spacing: 0.005em;
  opacity: 0.85;
}

/* ── CTA Actions ─────────────────────────────── */

.cta2-actions {
  display: flex;
  flex-direction: column;
  gap: 1rem;
  width: 100%;
}

.cta2-btn-primary {
  background: var(--foreground);
  color: var(--background);
  border-radius: 99px;
  padding: 0 1.5rem;
  height: 2.25rem;
  font-weight: 500;
  font-size: 0.8125rem;
  letter-spacing: 0.01em;
  transition: transform 0.2s ease, opacity 0.2s ease;
}

.cta2-btn-primary:hover {
  transform: scale(1.02);
  opacity: 0.9;
}

.cta2-btn-secondary {
  color: var(--foreground);
  border-radius: 99px;
  padding: 0 1.25rem;
  height: 2.25rem;
  font-weight: 400;
  font-size: 0.8125rem;
  letter-spacing: 0.01em;
  opacity: 0.7;
  transition: opacity 0.2s ease;
}

.cta2-btn-secondary:hover {
  opacity: 1;
}

/* ================================
   Responsive — Tablet (640px+)
   ================================ */

@container cta2 (min-width: 640px) {
  .cta2-actions {
    flex-direction: row;
    width: auto;
  }
}

/* ================================
   Responsive — Desktop (960px+)
   ================================ */

@container cta2 (min-width: 960px) {
  .cta2-card {
    min-height: 500px;
  }

  .cta2-content {
    padding: 6rem 4rem;
  }
}

/* ================================
   Large Desktop (1200px+)
   ================================ */

@container cta2 (min-width: 1200px) {
  .cta2-container {
    padding: 8rem 2rem;
  }
}
