import { FaqSectionV1 } from "../faq-section-v1"

export default function Page() {
    return <FaqSectionV1 />
}

"use client"

import { useState, useEffect, useRef, useCallback } from "react"
import "./faq-section-v1.css"
import { cn } from "@/lib/utils"
import { Plus, Minus } from "lucide-react"

/* ─── FAQ Data ───────────────────────────────── */

const faqItems = [
    {
        question: "Lorem ipsum dolor sit amet consectetur?",
        answer: "Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.",
    },
    {
        question: "Quis autem vel eum iure reprehenderit?",
        answer: "Nemo enim ipsam voluptatem quia voluptas sit aspernatur aut odit aut fugit, sed quia consequuntur magni dolores eos qui ratione voluptatem sequi nesciunt.",
    },
    {
        question: "Ut enim ad minima veniam nostrud exercitation?",
        answer: "Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident sunt in culpa.",
    },
    {
        question: "Neque porro quisquam est qui dolorem ipsum?",
        answer: "At vero eos et accusamus et iusto odio dignissimos ducimus qui blanditiis praesentium voluptatum deleniti atque corrupti quos dolores et quas molestias excepturi.",
    },
    {
        question: "Sed ut perspiciatis unde omnis iste natus?",
        answer: "Temporibus autem quibusdam et aut officiis debitis aut rerum necessitatibus saepe eveniet ut et voluptates repudiandae sint et molestiae non recusandae.",
    },
]

/* ─── Accordion Item ─────────────────────────── */

function AccordionItem({
    question,
    answer,
    isOpen,
    onToggle,
}: {
    question: string
    answer: string
    isOpen: boolean
    onToggle: () => void
}) {
    const contentRef = useRef<HTMLDivElement>(null)
    const [height, setHeight] = useState(0)

    useEffect(() => {
        if (contentRef.current) {
            setHeight(isOpen ? contentRef.current.scrollHeight : 0)
        }
    }, [isOpen])

    return (
        <div className="faq1-item">
            <button
                className="faq1-trigger"
                onClick={onToggle}
                aria-expanded={isOpen}
            >
                <span className="faq1-question">{question}</span>
                <span className="faq1-icon">
                    {isOpen ? (
                        <Minus className="faq1-icon-svg" />
                    ) : (
                        <Plus className="faq1-icon-svg" />
                    )}
                </span>
            </button>
            <div
                className="faq1-content-wrapper"
                style={{ height }}
            >
                <div ref={contentRef} className="faq1-content">
                    <p className="faq1-answer">{answer}</p>
                </div>
            </div>
        </div>
    )
}

/* ─── Main Component ─────────────────────────── */

export function FaqSectionV1({
    className,
    ...props
}: React.ComponentProps<"section">) {
    const [openIndex, setOpenIndex] = useState<number | null>(null)

    const handleToggle = useCallback((index: number) => {
        setOpenIndex((prev) => (prev === index ? null : index))
    }, [])

    return (
        <section
            className={cn("faq1-section", className)}
            {...props}
        >
            {/* Subtle grain texture */}
            <div className="faq1-grain" aria-hidden="true" />

            <div className="faq1-inner">
                {/* Eyebrow */}
                <p className="faq1-eyebrow">
                    FAQ
                </p>

                {/* Headline */}
                <h2 className="faq1-headline">
                    Frequently asked
                    <br />
                    <em>questions</em>
                </h2>

                {/* Description */}
                <p className="faq1-description">
                    Lorem ipsum dolor sit amet consectetur adipisicing elit. Quisquam, quos.
                </p>

                {/* Accordion */}
                <div className="faq1-accordion">
                    {faqItems.map((item, index) => (
                        <AccordionItem
                            key={index}
                            question={item.question}
                            answer={item.answer}
                            isOpen={openIndex === index}
                            onToggle={() => handleToggle(index)}
                        />
                    ))}
                </div>
            </div>
        </section>
    )
}

/* CSS Variables for FAQ Section V1 */
/* These variables are required for the component styles to work correctly */
/* Import this file in your globals.css or app layout */

/* Fonts — inherits --font-sans from next/font (globals.css) */

/* ============================================
   FAQ Section V1 — Editorial Accordion
   Refined serif headline, smooth accordion
   with plus/minus toggles, subtle grain texture
   ============================================ */

/* ── Section Root ──────────────────────────── */

.faq1-section {
  /* Fonts */
  --faq1-display: var(--font-display, var(--font-instrument-serif), serif);
  --faq1-sans: var(--font-sans, 'Geist', sans-serif);

  /* Colors */
  --faq1-divider: oklch(0 0 0 / 0.08);
  --faq1-icon-stroke: oklch(0 0 0 / 0.35);
  --faq1-hover-bg: oklch(0 0 0 / 0.02);

  position: relative;
  width: 100%;
  overflow: hidden;
  container-type: inline-size;
  container-name: faq1;
  color: var(--foreground);
}

.dark .faq1-section {
  --faq1-divider: oklch(1 0 0 / 0.08);
  --faq1-icon-stroke: oklch(1 0 0 / 0.35);
  --faq1-hover-bg: oklch(1 0 0 / 0.03);
}

/* ── Grain Overlay ───────────────────────────── */

.faq1-grain {
  position: absolute;
  inset: 0;
  z-index: 0;
  pointer-events: none;
  opacity: 0.3;
  background: url("data:image/svg+xml,%3Csvg viewBox='0 0 256 256' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='faq1-grain'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.8' numOctaves='4' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23faq1-grain)'/%3E%3C/svg%3E");
  background-size: 180px 180px;
  mix-blend-mode: overlay;
}

.dark .faq1-grain {
  opacity: 0.15;
  mix-blend-mode: soft-light;
}

/* ── Inner Content ───────────────────────────── */

.faq1-inner {
  position: relative;
  z-index: 1;
  max-width: 48rem;
  margin: 0 auto;
  padding: 5rem 1.25rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
}

/* ── Eyebrow ─────────────────────────────────── */

.faq1-eyebrow {
  font-family: var(--faq1-sans);
  font-size: 0.6875rem;
  font-weight: 500;
  letter-spacing: 0.18em;
  text-transform: uppercase;
  color: var(--muted-foreground);
  margin: 0 0 1.25rem;
}

/* ── Headline ────────────────────────────────── */

.faq1-headline {
  font-family: var(--faq1-display);
  font-size: clamp(2rem, 5.5vw, 3.25rem);
  font-weight: 400;
  font-style: normal;
  line-height: 1.15;
  letter-spacing: -0.01em;
  color: var(--foreground);
  margin: 0 0 1rem;
}

.faq1-headline em {
  font-style: italic;
  color: var(--foreground);
}

/* ── Description ─────────────────────────────── */

.faq1-description {
  font-family: var(--faq1-sans);
  font-size: 0.9375rem;
  line-height: 1.7;
  color: var(--muted-foreground);
  max-width: 32rem;
  margin: 0 0 3rem;
}

/* ── Accordion Container ─────────────────────── */

.faq1-accordion {
  width: 100%;
  text-align: left;
}

/* ── Accordion Item ──────────────────────────── */

.faq1-item {
  border-top: 1px solid var(--faq1-divider);
}

.faq1-item:last-child {
  border-bottom: 1px solid var(--faq1-divider);
}

/* ── Trigger Button ──────────────────────────── */

.faq1-trigger {
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 1.5rem;
  padding: 1.25rem 0.25rem;
  background: none;
  border: none;
  cursor: pointer;
  text-align: left;
  border-radius: 0.25rem;
  transition: background-color 0.2s ease;
}

.faq1-trigger:hover {
  background-color: var(--faq1-hover-bg);
}

.faq1-trigger:focus-visible {
  outline: 2px solid var(--ring);
  outline-offset: 2px;
}

/* ── Question Text ───────────────────────────── */

.faq1-question {
  font-family: var(--faq1-sans);
  font-size: 1rem;
  font-weight: 500;
  line-height: 1.4;
  letter-spacing: -0.01em;
  color: var(--foreground);
}

/* ── Plus/Minus Icon ─────────────────────────── */

.faq1-icon {
  flex-shrink: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--faq1-icon-stroke);
}

.faq1-icon-svg {
  width: 1.125rem;
  height: 1.125rem;
}

/* ── Content Wrapper (height transition) ─────── */

.faq1-content-wrapper {
  overflow: hidden;
  transition: height 0.35s cubic-bezier(0.16, 1, 0.3, 1);
}

/* ── Answer Content ──────────────────────────── */

.faq1-content {
  padding: 0 0.25rem 1.25rem;
}

.faq1-answer {
  font-family: var(--faq1-sans);
  font-size: 0.875rem;
  line-height: 1.7;
  color: var(--muted-foreground);
  margin: 0;
  max-width: 38rem;
}

/* ================================
   Responsive — Tablet (640px+)
   ================================ */

@container faq1 (min-width: 640px) {
  .faq1-inner {
    padding: 6rem 2rem;
  }

  .faq1-description {
    font-size: 1rem;
    margin-bottom: 3.5rem;
  }

  .faq1-trigger {
    padding: 1.5rem 0.5rem;
  }

  .faq1-question {
    font-size: 1.0625rem;
  }

  .faq1-content {
    padding: 0 0.5rem 1.5rem;
  }

  .faq1-answer {
    font-size: 0.9375rem;
  }
}

/* ================================
   Responsive — Desktop (960px+)
   ================================ */

@container faq1 (min-width: 960px) {
  .faq1-inner {
    padding: 8rem 2.5rem;
  }

  .faq1-description {
    margin-bottom: 4rem;
  }

  .faq1-trigger {
    padding: 1.75rem 0.75rem;
  }

  .faq1-question {
    font-size: 1.125rem;
  }

  .faq1-answer {
    font-size: 1rem;
  }
}

/* ================================
   Large Desktop (1200px+)
   ================================ */

@container faq1 (min-width: 1200px) {
  .faq1-inner {
    padding: 10rem 3rem;
  }
}

/* ================================
   Accessibility — Reduced Motion
   ================================ */

@media (prefers-reduced-motion: reduce) {
  .faq1-content-wrapper {
    transition: none;
  }
}
