import { FeatureSectionV1 } from "../feature-section-v1"

export default function Page() {
    return <FeatureSectionV1 />
}

import "./feature-section-v1.css"
import { cn } from "@/lib/utils"

/* ─── Card Graphic (Unsplash images) ─────────── */

function CardGraphic({ src, alt }: { src: string; alt?: string }) {
    return (
        <div className="fsv1-graphic-wrapper">
            <div className="fsv1-graphic-overlay" aria-hidden="true" />
            <img
                src={src}
                alt={alt ?? ""}
                className="fsv1-graphic"
                aria-hidden="true"
            />
        </div>
    )
}

/* ─── Feature Card ───────────────────────────── */

function FeatureCard({
    index,
    graphic,
    title,
    description,
}: {
    index: string
    graphic: React.ReactNode
    title: string
    description: string
}) {
    return (
        <article className="fsv1-card">
            {/* Graphic with L-bracket corners */}
            <div className="fsv1-card-graphic">
                {graphic}
                <span className="fsv1-bracket fsv1-bracket--tl" aria-hidden="true" />
                <span className="fsv1-bracket fsv1-bracket--tr" aria-hidden="true" />
                <span className="fsv1-bracket fsv1-bracket--bl" aria-hidden="true" />
                <span className="fsv1-bracket fsv1-bracket--br" aria-hidden="true" />
            </div>

            {/* Footer content: badge left, title+desc right */}
            <div className="fsv1-card-content">
                <span className="fsv1-card-index">{index}</span>
                <div className="fsv1-card-text">
                    <h3 className="fsv1-card-title">{title}</h3>
                    <p className="fsv1-card-desc">{description}</p>
                </div>
            </div>
        </article>
    )
}

/* ─── Main Component ─────────────────────────── */

export function FeatureSectionV1({
    className,
    ...props
}: React.ComponentProps<"section">) {
    return (
        <section
            className={cn("fsv1-section", className)}
            {...props}
        >
            <div className="fsv1-inner">
                {/* Intro paragraph */}
                <p className="fsv1-intro">
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed
                    do eiusmod tempor incididunt ut labore et dolore magna aliqua.
                </p>

                {/* 2×2 Feature cards */}
                <div className="fsv1-cards">
                    <FeatureCard
                        index="01"
                        graphic={<CardGraphic src="https://cdn.cosmos.so/9756655e-96d3-4aea-9844-521793e68b66?format=jpeg" alt="Lorem ipsum" />}
                        title="Lorem ipsum dolor."
                        description="Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam."
                    />
                    <FeatureCard
                        index="02"
                        graphic={<CardGraphic src="https://cdn.cosmos.so/9756655e-96d3-4aea-9844-521793e68b66?format=jpeg" alt="Lorem ipsum" />}
                        title="Consectetur elit."
                        description="Quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat duis aute."
                    />
                    <FeatureCard
                        index="03"
                        graphic={<CardGraphic src="https://cdn.cosmos.so/9756655e-96d3-4aea-9844-521793e68b66?format=jpeg" alt="Lorem ipsum" />}
                        title="Sed do eiusmod."
                        description="Irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur."
                    />
                    <FeatureCard
                        index="04"
                        graphic={<CardGraphic src="https://cdn.cosmos.so/9756655e-96d3-4aea-9844-521793e68b66?format=jpeg" alt="Lorem ipsum" />}
                        title="Tempor incididunt."
                        description="Excepteur sint occaecat cupidatat non proident sunt in culpa qui officia deserunt mollit."
                    />
                </div>
            </div>
        </section>
    )
}

/* CSS Variables for Feature Section V1 */
/* These variables are required for the component styles to work correctly */
/* Import this file in your globals.css or app layout */

/* Fonts — inherits --font-sans from next/font (globals.css) */

/* ============================================
   Feature Section V1 — 2×2 Compact Card Grid
   Large intro paragraph, four compact
   feature cards with L-bracket corners, 2-row × 2-column layout.
   ============================================ */

.fsv1-section {
  /* Fonts — uses next/font CSS variables for zero-layout-shift loading */
  --fsv1-display: var(--fsv1-sans);
  --fsv1-sans: var(--font-sans, 'Geist', sans-serif);

  /* Colors */
  --fsv1-accent: oklch(0.55 0.22 264.53);
  --fsv1-border: oklch(0 0 0 / 0.1);
  --fsv1-card-bg: oklch(0 0 0 / 0.025);
  --fsv1-bracket: oklch(0 0 0 / 0.16);
  --fsv1-surface: var(--background, oklch(0.97 0 0));
  color: var(--foreground);
  container-type: inline-size;
  container-name: fsv1;
  width: 100%;
}

.dark .fsv1-section {
  --fsv1-accent: oklch(0.68 0.19 260);
  --fsv1-border: oklch(1 0 0 / 0.1);
  --fsv1-card-bg: oklch(1 0 0 / 0.03);
  --fsv1-bracket: oklch(1 0 0 / 0.16);
  --fsv1-surface: var(--background, oklch(0.17 0 0));
}

.fsv1-inner {
  max-width: 64rem;
  margin: 0 auto;
  padding: 5rem 1.25rem;
}

/* ================================
   Intro Paragraph
   ================================ */

.fsv1-intro {
  font-family: var(--fsv1-display);
  font-size: clamp(1.25rem, 3.2vw, 2rem);
  font-weight: 500;
  line-height: 1.3;
  letter-spacing: -0.01em;
  max-width: 46rem;
  margin-bottom: 2.5rem;
}

/* ================================
   Cards Container — 2×2 Grid
   ================================ */

.fsv1-cards {
  display: grid;
  grid-template-columns: 1fr;
  gap: 0.75rem;
  border: 1px dotted var(--fsv1-border);
  border-radius: 0.875rem;
  padding: 0.75rem;
}

/* ================================
   Feature Card — Compact
   ================================ */

.fsv1-card {
  position: relative;
  background: var(--fsv1-card-bg);
  border-radius: 0.625rem;
  padding: 0 1rem 1rem;
  overflow: hidden;
}

/* ─── L-Bracket Corners (per card) ────────── */

.fsv1-bracket {
  position: absolute;
  width: 14px;
  height: 14px;
  pointer-events: none;
  z-index: 40;
}

.fsv1-bracket--tl {
  top: 6px;
  left: 6px;
  border-top: 1.5px solid var(--fsv1-bracket);
  border-left: 1.5px solid var(--fsv1-bracket);
}

.fsv1-bracket--tr {
  top: 6px;
  right: 6px;
  border-top: 1.5px solid var(--fsv1-bracket);
  border-right: 1.5px solid var(--fsv1-bracket);
}

.fsv1-bracket--bl {
  bottom: 6px;
  left: 6px;
  border-bottom: 1.5px solid var(--fsv1-bracket);
  border-left: 1.5px solid var(--fsv1-bracket);
}

.fsv1-bracket--br {
  bottom: 6px;
  right: 6px;
  border-bottom: 1.5px solid var(--fsv1-bracket);
  border-right: 1.5px solid var(--fsv1-bracket);
}

/* ─── Card Graphic Area ──────────── */

.fsv1-card-graphic {
  position: relative;
  margin: 0 -1rem;
  padding: 1.25rem;
}

.fsv1-graphic-wrapper {
  position: relative;
  overflow: hidden;
  border-radius: 0.375rem;
}


.fsv1-graphic {
  position: relative;
  z-index: 20;
  aspect-ratio: 2 / 1;
  width: 100%;
  object-fit: cover;
  display: block;
}

/* ─── Card Content (badge left, text right) ── */

.fsv1-card-content {
  display: flex;
  align-items: flex-start;
  gap: 0.75rem;
  padding: 0.75rem 0.25rem 0.25rem;
}

.fsv1-card-index {
  flex-shrink: 0;
  font-family: var(--fsv1-sans);
  font-size: 0.6875rem;
  font-weight: 500;
  color: var(--muted-foreground, oklch(0.44 0 0));
  opacity: 0.65;
  letter-spacing: 0.02em;
  line-height: 1.3;
  padding-top: 0.125rem;
}

.fsv1-card-text {
  flex: 1;
  min-width: 0;
}

.fsv1-card-title {
  font-family: var(--fsv1-sans);
  font-size: 1rem;
  font-weight: 600;
  letter-spacing: -0.02em;
  line-height: 1.3;
  color: var(--foreground, oklch(0 0 0));
  margin-bottom: 0.25rem;
}

.fsv1-card-desc {
  font-family: var(--fsv1-sans);
  font-size: 0.8125rem;
  line-height: 1.55;
  font-style: italic;
  color: var(--muted-foreground, oklch(0.44 0 0));
}

/* ================================
   Responsive — Tablet (640px+)
   2×2 grid kicks in
   ================================ */

@container fsv1 (min-width: 640px) {
  .fsv1-inner {
    padding: 6rem 2rem;
  }

  .fsv1-intro {
    margin-bottom: 3rem;
  }

  .fsv1-cards {
    grid-template-columns: repeat(2, 1fr);
  }

  .fsv1-card {
    padding: 0 1.25rem 1.25rem;
  }

  .fsv1-card-graphic {
    margin: 0 -1.25rem;
    padding: 1.5rem;
  }
}

/* ================================
   Responsive — Desktop (960px+)
   ================================ */

@container fsv1 (min-width: 960px) {
  .fsv1-inner {
    padding: 7rem 2.5rem;
  }

  .fsv1-intro {
    margin-bottom: 3.5rem;
  }

  .fsv1-card {
    padding: 0 1.5rem 1.5rem;
  }

  .fsv1-card-graphic {
    margin: 0 -1.5rem;
    padding: 1.75rem;
  }

  .fsv1-card-title {
    font-size: 1.0625rem;
  }

  .fsv1-card-desc {
    font-size: 0.875rem;
  }
}

/* ================================
   Large Desktop (1200px+)
   ================================ */

@container fsv1 (min-width: 1200px) {
  .fsv1-card {
    padding: 0 1.75rem 1.75rem;
  }

  .fsv1-card-graphic {
    margin: 0 -1.75rem;
    padding: 2rem;
  }

  .fsv1-card-title {
    font-size: 1.125rem;
  }
}
