import { FeatureSectionV2 } from "../feature-section-v2"

export default function Page() {
    return <FeatureSectionV2 />
}

import "./feature-section-v2.css"
import { cn } from "@/lib/utils"

export function FeatureSectionV2({
    className,
    ...props
}: React.ComponentProps<"div">) {
    const features = [
        {
            title: "Lorem Ipsum",
            description:
                "Dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.",
            visual: "bolt",
            span: true,
        },
        {
            title: "Consectetur Elit",
            description:
                "Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo.",
            visual: "lock",
            span: false,
        },
        {
            title: "Sed Do Eiusmod",
            description:
                "Consequat duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore.",
            visual: "radial",
            span: false,
        },
        {
            title: "Tempor Incididunt",
            description:
                "Excepteur sint occaecat cupidatat non proident sunt in culpa qui officia deserunt mollit anim.",
            visual: "globe",
            span: false,
        },
        {
            title: "Ut Labore",
            description:
                "Et dolore magna aliqua. Ut enim ad minim veniam quis nostrud exercitation ullamco laboris.",
            visual: "noconfig",
            span: false,
        },
    ]

    return (
        <div
            className={cn(
                "w-full fsv2-container relative overflow-hidden",
                className
            )}
            {...props}
        >
            <div className="relative z-10 flex w-full flex-col items-center px-6 py-20 sm:py-28">
                <div className="w-full max-w-5xl">
                    <header
                        className="fsv2-header fsv2-animate mb-14 sm:mb-16"
                        style={{ animationDelay: "0s" }}
                    >
                        <h2 className="fsv2-heading text-foreground">
                            Lorem ipsum dolor
                        </h2>
                        <p className="fsv2-description text-muted-foreground">
                            Sed do eiusmod tempor incididunt ut labore et dolore
                            magna aliqua. Ut enim ad minim veniam quis nostrud.
                        </p>
                    </header>

                    <div className="fsv2-bento">
                        {features.map((feature, index) => (
                            <article
                                key={feature.title}
                                className={cn(
                                    "fsv2-card fsv2-animate",
                                    feature.span && "fsv2-card-wide"
                                )}
                                style={{
                                    animationDelay: `${0.1 + index * 0.07}s`,
                                }}
                            >
                                <div
                                    className={cn(
                                        "fsv2-visual",
                                        `fsv2-visual-${feature.visual}`
                                    )}
                                    aria-hidden="true"
                                >
                                    {/* Blazing Fast — Lightning Bolt */}
                                    {feature.visual === "bolt" && (
                                        <>
                                            <span className="fsv2-bolt-glow" />
                                            <span className="fsv2-bolt" />
                                        </>
                                    )}
                                    {/* Vault Security — Lock Rings */}
                                    {feature.visual === "lock" && (
                                        <>
                                            <span className="fsv2-lock fsv2-lock-1" />
                                            <span className="fsv2-lock fsv2-lock-2" />
                                            <span className="fsv2-lock fsv2-lock-3" />
                                            <span className="fsv2-lock-center" />
                                        </>
                                    )}
                                    {/* Live Analytics — Radial Chart */}
                                    {feature.visual === "radial" && (
                                        <>
                                            <span className="fsv2-radial" />
                                            <span className="fsv2-radial-hole" />
                                            <span className="fsv2-radial-dot" />
                                        </>
                                    )}
                                    {/* Global Edge — Wireframe Globe */}
                                    {feature.visual === "globe" && (
                                        <>
                                            <span className="fsv2-globe fsv2-globe-outer" />
                                            <span className="fsv2-globe fsv2-globe-m1" />
                                            <span className="fsv2-globe fsv2-globe-m2" />
                                            <span className="fsv2-globe-eq" />
                                            <span className="fsv2-globe-dot" />
                                        </>
                                    )}
                                    {/* Zero Config — Circle with Slash */}
                                    {feature.visual === "noconfig" && (
                                        <>
                                            <span className="fsv2-noconfig-circle" />
                                            <span className="fsv2-noconfig-slash" />
                                        </>
                                    )}
                                </div>
                                <div className="fsv2-card-text">
                                    <h3 className="fsv2-card-title text-foreground">
                                        {feature.title}
                                    </h3>
                                    <p className="fsv2-card-desc text-muted-foreground">
                                        {feature.description}
                                    </p>
                                </div>
                            </article>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    )
}

/* CSS Variables for Feature Section V2 */
/* These variables are required for the component styles to work correctly */
/* Import this file in your globals.css or app layout */

/* Fonts — inherits --font-sans from next/font (globals.css) */

/* ============================================
   Feature Section V2 — Bento Visual Grid
   Asymmetric bento layout where each card has
   a unique animated CSS visual that matches
   its feature concept. No icons — pure geometry.
   ============================================ */

/* Fonts — inherits --font-sans from next/font (globals.css) */

.fsv2-container {
  container-type: inline-size;
  container-name: fsv2;
}

/* Entry animation */
@keyframes fsv2-enter {
  from {
    opacity: 0;
    transform: translateY(20px) scale(0.97);
  }
  to {
    opacity: 1;
    transform: translateY(0) scale(1);
  }
}

.fsv2-animate {
  animation: fsv2-enter 0.6s cubic-bezier(0.22, 1, 0.36, 1) both;
}

/* Header — centered, geometric sans */
.fsv2-header {
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  gap: 0.75rem;
}

.fsv2-heading {
  font-family: var(--font-sans);
  font-size: clamp(2rem, 5vw, 3rem);
  font-weight: 500;
  letter-spacing: -0.035em;
  line-height: 1.1;
}

.fsv2-description {
  font-family: var(--font-sans);
  font-size: 0.875rem;
  font-weight: 400;
  line-height: 1.7;
  letter-spacing: 0.06em;
  max-width: 30rem;
  margin-top: 0.25rem;
}

/* ================================
   Bento Grid
   ================================ */
.fsv2-bento {
  display: grid;
  grid-template-columns: 1fr;
  gap: 0.75rem;
}

@container fsv2 (min-width: 540px) {
  .fsv2-bento {
    grid-template-columns: repeat(2, 1fr);
    gap: 0.875rem;
  }

  .fsv2-card-wide {
    grid-column: 1 / -1;
  }
}

@container fsv2 (min-width: 768px) {
  .fsv2-bento {
    grid-template-columns: repeat(3, 1fr);
    gap: 1rem;
  }

  .fsv2-card-wide {
    grid-column: span 2;
  }
}

/* ================================
   Cards
   ================================ */
.fsv2-card {
  position: relative;
  display: flex;
  flex-direction: column;
  gap: 0;
  border-radius: 1rem;
  border: 1px solid oklch(0 0 0 / 0.06);
  background: oklch(0 0 0 / 0.015);
  overflow: hidden;
  transition:
    border-color 0.3s ease,
    box-shadow 0.3s ease,
    transform 0.3s ease;
}

.dark .fsv2-card {
  border-color: oklch(1 0 0 / 0.07);
  background: oklch(1 0 0 / 0.025);
}

.fsv2-card:hover {
  border-color: oklch(0 0 0 / 0.12);
  box-shadow: 0 16px 48px -16px oklch(0 0 0 / 0.1);
  transform: translateY(-3px);
}

.dark .fsv2-card:hover {
  border-color: oklch(1 0 0 / 0.14);
  box-shadow: 0 16px 48px -16px oklch(0 0 0 / 0.5);
}

/* ================================
   Visual Area
   ================================ */
.fsv2-visual {
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  height: 140px;
  overflow: hidden;
  background: oklch(0 0 0 / 0.02);
  transition: background 0.3s ease;
}

.dark .fsv2-visual {
  background: oklch(1 0 0 / 0.02);
}

/* Grain texture overlay on visual area */
.fsv2-visual::before {
  content: "";
  position: absolute;
  inset: 0;
  background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 256 256' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='n'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.82' numOctaves='4' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23n)' opacity='0.06'/%3E%3C/svg%3E");
  background-repeat: repeat;
  background-size: 180px 180px;
  pointer-events: none;
  z-index: 0;
  opacity: 0.6;
  mix-blend-mode: multiply;
}

.dark .fsv2-visual::before {
  mix-blend-mode: soft-light;
  opacity: 0.5;
}

.fsv2-card:hover .fsv2-visual {
  background: oklch(0 0 0 / 0.035);
}

.dark .fsv2-card:hover .fsv2-visual {
  background: oklch(1 0 0 / 0.04);
}

.fsv2-card-wide .fsv2-visual {
  height: 160px;
}

/* Text area — subtle gradient bg, flex-grows to fill card height */
.fsv2-card-text {
  padding: 1.25rem 1.5rem 1.5rem;
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
  flex: 1;
  background: linear-gradient(
    to bottom,
    oklch(0 0 0 / 0.01) 0%,
    oklch(0 0 0 / 0.035) 100%
  );
}

.dark .fsv2-card-text {
  background: linear-gradient(
    to bottom,
    oklch(1 0 0 / 0.01) 0%,
    oklch(1 0 0 / 0.04) 100%
  );
}

.fsv2-card-title {
  font-family: var(--font-sans);
  font-size: 1rem;
  font-weight: 600;
  letter-spacing: -0.02em;
  line-height: 1.3;
}

.fsv2-card-desc {
  font-family: var(--font-sans);
  font-size: 0.8125rem;
  line-height: 1.55;
  letter-spacing: 0.01em;
}

/* ================================================================
   1. LIGHTNING BOLT — Blazing Fast
   A sharp bolt shape built with clip-path, backed by a
   soft radial glow. A double-flash animation simulates
   a real lightning strike. On hover, bolt & glow intensify.
   ================================================================ */

/* Glow behind the bolt */
@keyframes fsv2-bolt-glow-pulse {
  0%,
  100% {
    opacity: 0.3;
    transform: scale(1);
  }
  50% {
    opacity: 0.6;
    transform: scale(1.08);
  }
}

.fsv2-bolt-glow {
  position: absolute;
  width: 90px;
  height: 90px;
  border-radius: 50%;
  background: radial-gradient(
    circle,
    oklch(0 0 0 / 0.06) 0%,
    transparent 70%
  );
  animation: fsv2-bolt-glow-pulse 3s ease-in-out infinite;
  transition:
    opacity 0.4s ease,
    transform 0.4s ease;
}

.dark .fsv2-bolt-glow {
  background: radial-gradient(
    circle,
    oklch(1 0 0 / 0.07) 0%,
    transparent 70%
  );
}

.fsv2-card:hover .fsv2-bolt-glow {
  opacity: 1;
  transform: scale(1.25);
}

/* Lightning bolt shape */
@keyframes fsv2-bolt-flash {
  0%,
  82%,
  100% {
    opacity: 0.55;
  }
  86% {
    opacity: 1;
  }
  89% {
    opacity: 0.45;
  }
  93% {
    opacity: 1;
  }
}

.fsv2-bolt {
  position: absolute;
  width: 42px;
  height: 72px;
  background: oklch(0 0 0 / 0.14);
  clip-path: polygon(
    48% 0%,
    8% 50%,
    36% 50%,
    18% 100%,
    92% 38%,
    58% 38%,
    76% 0%
  );
  animation: fsv2-bolt-flash 3.5s ease-in-out infinite;
  transition:
    background 0.4s ease,
    transform 0.4s ease;
  z-index: 1;
}

.dark .fsv2-bolt {
  background: oklch(1 0 0 / 0.18);
}

.fsv2-card:hover .fsv2-bolt {
  background: oklch(0 0 0 / 0.25);
  transform: scale(1.08);
}

.dark .fsv2-card:hover .fsv2-bolt {
  background: oklch(1 0 0 / 0.32);
}

/* ================================================================
   2. LOCK RINGS — Vault Security
   Concentric dashed rings spinning in alternating directions.
   On hover, rings stop spinning and borders become solid = locked.
   ================================================================ */

@keyframes fsv2-lock-cw {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

@keyframes fsv2-lock-ccw {
  from { transform: rotate(360deg); }
  to { transform: rotate(0deg); }
}

.fsv2-lock {
  position: absolute;
  border-radius: 50%;
  border: 1.5px dashed oklch(0 0 0 / 0.1);
  transition:
    border-style 0.4s ease,
    border-color 0.4s ease;
}

.dark .fsv2-lock {
  border-color: oklch(1 0 0 / 0.12);
}

.fsv2-lock-1 {
  width: 36px;
  height: 36px;
  animation: fsv2-lock-cw 5s linear infinite;
}

.fsv2-lock-2 {
  width: 68px;
  height: 68px;
  border-width: 1.5px;
  animation: fsv2-lock-ccw 9s linear infinite;
}

.fsv2-lock-3 {
  width: 100px;
  height: 100px;
  border-width: 1px;
  animation: fsv2-lock-cw 14s linear infinite;
}

/* Lock center dot */
.fsv2-lock-center {
  position: absolute;
  width: 8px;
  height: 8px;
  border-radius: 50%;
  background: oklch(0 0 0 / 0.15);
  transition:
    background 0.4s ease,
    transform 0.4s ease;
}

.dark .fsv2-lock-center {
  background: oklch(1 0 0 / 0.25);
}

/* Hover: rings lock into place */
.fsv2-card:hover .fsv2-lock {
  border-style: solid;
  border-color: oklch(0 0 0 / 0.18);
  animation-play-state: paused;
}

.dark .fsv2-card:hover .fsv2-lock {
  border-color: oklch(1 0 0 / 0.22);
}

.fsv2-card:hover .fsv2-lock-center {
  background: oklch(0 0 0 / 0.3);
  transform: scale(1.3);
}

.dark .fsv2-card:hover .fsv2-lock-center {
  background: oklch(1 0 0 / 0.45);
}

/* ================================================================
   3. RADIAL CHART — Live Analytics
   A donut chart with 4 unequal segments separated by thin gaps.
   Slow rotation animation. On hover, stops and intensifies.
   ================================================================ */

@keyframes fsv2-radial-spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

.fsv2-radial {
  position: absolute;
  width: 76px;
  height: 76px;
  border-radius: 50%;
  background: conic-gradient(
    oklch(0 0 0 / 0.20) 0deg 108deg,
    transparent 108deg 111deg,
    oklch(0 0 0 / 0.10) 111deg 198deg,
    transparent 198deg 201deg,
    oklch(0 0 0 / 0.16) 201deg 294deg,
    transparent 294deg 297deg,
    oklch(0 0 0 / 0.07) 297deg 357deg,
    transparent 357deg 360deg
  );
  mask: radial-gradient(circle, transparent 38%, black 39%);
  -webkit-mask: radial-gradient(circle, transparent 38%, black 39%);
  animation: fsv2-radial-spin 20s linear infinite;
  transition:
    transform 0.4s ease,
    filter 0.4s ease;
}

.dark .fsv2-radial {
  background: conic-gradient(
    oklch(1 0 0 / 0.22) 0deg 108deg,
    transparent 108deg 111deg,
    oklch(1 0 0 / 0.12) 111deg 198deg,
    transparent 198deg 201deg,
    oklch(1 0 0 / 0.18) 201deg 294deg,
    transparent 294deg 297deg,
    oklch(1 0 0 / 0.08) 297deg 357deg,
    transparent 357deg 360deg
  );
}

/* Center hole overlay (fallback for browsers without mask) */
.fsv2-radial-hole {
  position: absolute;
  width: 30px;
  height: 30px;
  border-radius: 50%;
  border: 1px solid oklch(0 0 0 / 0.05);
  transition: border-color 0.4s ease;
}

.dark .fsv2-radial-hole {
  border-color: oklch(1 0 0 / 0.06);
}

/* Center dot */
.fsv2-radial-dot {
  position: absolute;
  width: 5px;
  height: 5px;
  border-radius: 50%;
  background: oklch(0 0 0 / 0.18);
  transition:
    background 0.4s ease,
    transform 0.4s ease;
}

.dark .fsv2-radial-dot {
  background: oklch(1 0 0 / 0.25);
}

/* Hover: stop rotation, sharpen */
.fsv2-card:hover .fsv2-radial {
  animation-play-state: paused;
}

.fsv2-card:hover .fsv2-radial-hole {
  border-color: oklch(0 0 0 / 0.1);
}

.dark .fsv2-card:hover .fsv2-radial-hole {
  border-color: oklch(1 0 0 / 0.12);
}

.fsv2-card:hover .fsv2-radial-dot {
  background: oklch(0 0 0 / 0.3);
  transform: scale(1.4);
}

.dark .fsv2-card:hover .fsv2-radial-dot {
  background: oklch(1 0 0 / 0.4);
}

/* ================================================================
   4. WIREFRAME GLOBE — Global Edge
   CSS globe using ellipses as meridian lines + equator.
   Meridians animate scaleX to simulate rotation.
   An orbiting dot circles the equator.
   ================================================================ */

.fsv2-globe {
  position: absolute;
  border-radius: 50%;
  border: 1px solid oklch(0 0 0 / 0.08);
}

.dark .fsv2-globe {
  border-color: oklch(1 0 0 / 0.1);
}

/* Outer sphere */
.fsv2-globe-outer {
  width: 84px;
  height: 84px;
}

/* Meridian lines — ellipses that oscillate scaleX */
@keyframes fsv2-meridian-a {
  0%,
  100% {
    transform: scaleX(0.2);
  }
  50% {
    transform: scaleX(0.85);
  }
}

@keyframes fsv2-meridian-b {
  0%,
  100% {
    transform: scaleX(0.7);
  }
  50% {
    transform: scaleX(0.15);
  }
}

.fsv2-globe-m1 {
  width: 84px;
  height: 84px;
  transform: scaleX(0.2);
  animation: fsv2-meridian-a 7s ease-in-out infinite;
}

.fsv2-globe-m2 {
  width: 84px;
  height: 84px;
  transform: scaleX(0.7);
  animation: fsv2-meridian-b 7s ease-in-out infinite;
}

/* Equator — horizontal ellipse */
.fsv2-globe-eq {
  position: absolute;
  width: 84px;
  height: 28px;
  border-radius: 50%;
  border: 1px solid oklch(0 0 0 / 0.06);
}

.dark .fsv2-globe-eq {
  border-color: oklch(1 0 0 / 0.08);
}

/* Orbiting dot */
@keyframes fsv2-globe-orbit {
  from {
    transform: rotate(0deg) translateX(42px) rotate(0deg);
  }
  to {
    transform: rotate(360deg) translateX(42px) rotate(-360deg);
  }
}

.fsv2-globe-dot {
  position: absolute;
  width: 6px;
  height: 6px;
  border-radius: 50%;
  background: oklch(0 0 0 / 0.22);
  animation: fsv2-globe-orbit 6s linear infinite;
}

.dark .fsv2-globe-dot {
  background: oklch(1 0 0 / 0.35);
}

/* Hover: intensify */
.fsv2-card:hover .fsv2-globe {
  border-color: oklch(0 0 0 / 0.14);
}

.dark .fsv2-card:hover .fsv2-globe {
  border-color: oklch(1 0 0 / 0.18);
}

.fsv2-card:hover .fsv2-globe-eq {
  border-color: oklch(0 0 0 / 0.1);
}

.dark .fsv2-card:hover .fsv2-globe-eq {
  border-color: oklch(1 0 0 / 0.14);
}

.fsv2-card:hover .fsv2-globe-dot {
  background: oklch(0 0 0 / 0.35);
}

.dark .fsv2-card:hover .fsv2-globe-dot {
  background: oklch(1 0 0 / 0.5);
}

/* ================================================================
   5. CIRCLE WITH SLASH — Zero Config
   A prohibition-style symbol: circle + diagonal slash.
   Means "no configuration needed." Subtle pulse animation.
   On hover, the symbol sharpens and becomes more prominent.
   ================================================================ */

@keyframes fsv2-noconfig-pulse {
  0%,
  100% {
    transform: scale(1);
    opacity: 0.5;
  }
  50% {
    transform: scale(1.03);
    opacity: 0.75;
  }
}

@keyframes fsv2-noconfig-slash-pulse {
  0%,
  100% {
    transform: rotate(45deg) scale(1);
    opacity: 0.5;
  }
  50% {
    transform: rotate(45deg) scale(1.03);
    opacity: 0.75;
  }
}

.fsv2-noconfig-circle {
  position: absolute;
  width: 64px;
  height: 64px;
  border-radius: 50%;
  border: 2px solid oklch(0 0 0 / 0.12);
  animation: fsv2-noconfig-pulse 3.5s ease-in-out infinite;
  transition:
    border-color 0.4s ease,
    transform 0.4s ease;
}

.dark .fsv2-noconfig-circle {
  border-color: oklch(1 0 0 / 0.14);
}

.fsv2-noconfig-slash {
  position: absolute;
  width: 2px;
  height: 64px;
  border-radius: 1px;
  background: oklch(0 0 0 / 0.12);
  transform: rotate(45deg);
  animation: fsv2-noconfig-slash-pulse 3.5s ease-in-out infinite;
  transition:
    background 0.4s ease,
    transform 0.4s ease;
}

.dark .fsv2-noconfig-slash {
  background: oklch(1 0 0 / 0.14);
}

/* Hover: sharpen */
.fsv2-card:hover .fsv2-noconfig-circle {
  border-color: oklch(0 0 0 / 0.22);
  transform: scale(1.06);
  animation: none;
}

.dark .fsv2-card:hover .fsv2-noconfig-circle {
  border-color: oklch(1 0 0 / 0.26);
}

.fsv2-card:hover .fsv2-noconfig-slash {
  background: oklch(0 0 0 / 0.22);
  transform: rotate(45deg) scaleY(1.06);
  animation: none;
}

.dark .fsv2-card:hover .fsv2-noconfig-slash {
  background: oklch(1 0 0 / 0.26);
}

/* ================================
   Responsive
   ================================ */
@container fsv2 (min-width: 768px) {
  .fsv2-card-text {
    padding: 1.25rem 1.5rem 1.75rem;
  }

  .fsv2-card-wide .fsv2-visual {
    height: 180px;
  }

  .fsv2-card-title {
    font-size: 1.0625rem;
  }
}
