import { FeatureSectionV3 } from "../feature-section-v3"

export default function Page() {
    return <FeatureSectionV3 />
}

import "./feature-section-v3.css"
import { cn } from "@/lib/utils"

/* ─── Feature Data ───────────────────────────── */

const features = [
    {
        index: "01",
        title: "Lorem ipsum dolor sit amet",
        body: "Consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.",
    },
    {
        index: "02",
        title: "Ut enim ad minim veniam",
        body: "Quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat duis aute irure.",
    },
    {
        index: "03",
        title: "Excepteur sint occaecat cupidatat",
        body: "Non proident sunt in culpa qui officia deserunt mollit anim id est laborum sed ut perspiciatis.",
    },
    {
        index: "04",
        title: "Nemo enim ipsam voluptatem",
        body: "Quia voluptas sit aspernatur aut odit aut fugit sed quia consequuntur magni dolores eos qui ratione.",
    },
]

/* ─── Feature Row ───────────────────────────── */

function FeatureRow({
    index,
    title,
    body,
}: {
    index: string
    title: string
    body: string
}) {
    return (
        <li className="fsv3-row">
            <span className="fsv3-row-index" aria-hidden="true">
                {index}
            </span>
            <div className="fsv3-row-content">
                <h3 className="fsv3-row-title">{title}</h3>
                <p className="fsv3-row-body">{body}</p>
            </div>
        </li>
    )
}

/* ─── Main Component ─────────────────────────── */

export function FeatureSectionV3({
    className,
    ...props
}: React.ComponentProps<"section">) {
    return (
        <section
            className={cn("fsv3-section", className)}
            {...props}
        >
            <div className="fsv3-inner">
                <header className="fsv3-header">
                    <p className="fsv3-kicker">Consectetur adipiscing</p>
                    <h2 className="fsv3-heading">Lorem ipsum dolor sit amet</h2>
                    <p className="fsv3-subtitle">
                        Sed ut perspiciatis unde omnis iste natus error sit
                        voluptatem accusantium doloremque laudantium.
                    </p>
                </header>

                <ol className="fsv3-list">
                    {features.map((feature) => (
                        <FeatureRow
                            key={feature.title}
                            index={feature.index}
                            title={feature.title}
                            body={feature.body}
                        />
                    ))}
                </ol>
            </div>
        </section>
    )
}

/* CSS Variables for Feature Section V3 */
/* These variables are required for the component styles to work correctly */
/* Import this file in your globals.css or app layout */

/* ============================================
   Feature Section V3 — Editorial Feature Rail
   Left intro copy with right-side numbered list.
   Uses structure, spacing, and borders rather
   than filled background colors.
   ============================================ */

.fsv3-section {
  --fsv3-sans: var(--font-sans, "Geist", sans-serif);
  --fsv3-border: oklch(0 0 0 / 0.14);
  --fsv3-border-soft: oklch(0 0 0 / 0.08);

  color: var(--foreground);
  container-type: inline-size;
  container-name: fsv3;
  width: 100%;
}

.dark .fsv3-section {
  --fsv3-border: oklch(1 0 0 / 0.2);
  --fsv3-border-soft: oklch(1 0 0 / 0.11);
}

.fsv3-inner {
  max-width: 64rem;
  margin: 0 auto;
  padding: 5rem 1.25rem;
  display: grid;
  gap: 2.5rem;
}

.fsv3-header {
  display: grid;
  gap: 0.9rem;
}

.fsv3-kicker {
  font-family: var(--fsv3-sans);
  font-size: 0.7rem;
  letter-spacing: 0.18em;
  text-transform: uppercase;
  color: var(--muted-foreground);
  margin: 0;
}

.fsv3-heading {
  font-family: var(--fsv3-sans);
  font-size: clamp(2rem, 4.8vw, 3.3rem);
  font-weight: 400;
  line-height: 1.05;
  letter-spacing: -0.02em;
  margin: 0;
}

.fsv3-subtitle {
  font-family: var(--fsv3-sans);
  font-size: 0.95rem;
  font-weight: 400;
  line-height: 1.65;
  color: var(--muted-foreground);
  max-width: 34ch;
  margin: 0;
}

.fsv3-list {
  list-style: none;
  margin: 0;
  padding: 0 0 0 1.3rem;
  border-left: 1px solid var(--fsv3-border);
}

.fsv3-row {
  display: grid;
  grid-template-columns: auto 1fr;
  align-items: start;
  column-gap: 1rem;
  padding: 1.2rem 0;
  border-bottom: 1px solid var(--fsv3-border-soft);
}

.fsv3-row:last-child {
  border-bottom: none;
  padding-bottom: 0;
}

.fsv3-row-index {
  font-family: var(--fsv3-sans);
  font-size: 0.7rem;
  font-weight: 600;
  line-height: 1;
  letter-spacing: 0.08em;
  color: var(--muted-foreground);
  background: var(--muted);
  border: 1px solid var(--border);
  border-radius: 999px;
  min-width: 2.1rem;
  min-height: 2.1rem;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  margin: 0;
}

.fsv3-row-content {
  display: grid;
  gap: 0.45rem;
}

.fsv3-row-title {
  font-family: var(--fsv3-sans);
  font-size: 1.02rem;
  font-weight: 600;
  letter-spacing: -0.015em;
  line-height: 1.35;
  margin: 0;
}

.fsv3-row-body {
  font-family: var(--fsv3-sans);
  font-size: 0.875rem;
  line-height: 1.62;
  color: var(--muted-foreground);
  margin: 0;
}

@container fsv3 (min-width: 540px) {
  .fsv3-inner {
    padding: 6rem 2rem;
    gap: 3rem;
  }
}

@container fsv3 (min-width: 768px) {
  .fsv3-inner {
    padding: 6rem 2.5rem;
    gap: 4rem;
  }

  .fsv3-row {
    padding: 1.35rem 0;
  }

  .fsv3-row-title {
    font-size: 1.06rem;
  }
}

@container fsv3 (min-width: 900px) {
  .fsv3-inner {
    grid-template-columns: minmax(0, 0.9fr) minmax(0, 1.1fr);
    align-items: start;
  }
}

@container fsv3 (min-width: 1080px) {
  .fsv3-inner {
    padding: 7rem 3rem;
    column-gap: 5rem;
  }

  .fsv3-list {
    padding-left: 1.6rem;
  }

  .fsv3-row {
    column-gap: 1.2rem;
  }

  .fsv3-row-body {
    max-width: 52ch;
  }
}

/* ================================
   Accessibility — Reduced Motion
   ================================ */

@media (prefers-reduced-motion: reduce) {
  .fsv3-row {
    transition: none;
  }
}
