import { HeroSectionV2 } from "../hero-section-v2"

export default function Page() {
    return <HeroSectionV2 />
}

"use client"

import { useState } from "react"
import "./hero-section-v2.css"
import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"

const tabs = [
    "Lorem Ipsum",
    "Dolor Sit",
    "Amet Consec",
    "Adipiscing",
    "Elit Sed",
]

const PLACEHOLDER_IMAGE =
    "https://images.unsplash.com/photo-1604076850742-4c7221f3101b?q=80&w=1887&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D"

function PanelImage({
    src = PLACEHOLDER_IMAGE,
    alt,
    className,
}: {
    src?: string
    alt?: string
    className?: string
}) {
    return (
        <div className={cn("hs2-img-wrap", className)}>
            <div className="hs2-img-overlay" aria-hidden="true" />
            <img
                src={src}
                alt={alt ?? "Placeholder"}
                className="hs2-img"
            />
        </div>
    )
}

/* Tab 0 — Text left, image right */
function PanelHero() {
    return (
        <div className="hs2-panel hs2-panel-split">
            <div className="hs2-panel-col" style={{ gap: "0.625rem", justifyContent: "center" }}>
                <span className="hs2-panel-badge">Lorem</span>
                <h3 className="hs2-panel-heading">Lorem ipsum dolor sit amet</h3>
                <p className="hs2-panel-text">
                    Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
                    Ut enim ad minim veniam, quis nostrud exercitation.
                </p>
            </div>
            <PanelImage className="hs2-panel-image" alt="Lorem ipsum" />
        </div>
    )
}

/* Tab 1 — Image left, text right (reversed) */
function PanelDashboard() {
    return (
        <div className="hs2-panel hs2-panel-split hs2-panel-reverse">
            <PanelImage className="hs2-panel-image" alt="Dolor sit" />
            <div className="hs2-panel-col" style={{ gap: "0.625rem", justifyContent: "center" }}>
                <span className="hs2-panel-badge">Dolor</span>
                <h3 className="hs2-panel-heading">Dolor sit amet consectetur</h3>
                <p className="hs2-panel-text">
                    Ut enim ad minim veniam, quis nostrud exercitation ullamco
                    laboris nisi ut aliquip ex ea commodo consequat.
                </p>
            </div>
        </div>
    )
}

/* Tab 2 — Text with bullet list left, image right */
function PanelMedia() {
    return (
        <div className="hs2-panel hs2-panel-split">
            <div className="hs2-panel-col" style={{ gap: "0.625rem", justifyContent: "center" }}>
                <span className="hs2-panel-badge">Amet</span>
                <h3 className="hs2-panel-heading">Amet consectetur adipiscing</h3>
                <ul className="hs2-panel-list">
                    <li>Lorem ipsum dolor sit amet</li>
                    <li>Consectetur adipiscing elit sed</li>
                    <li>Eiusmod tempor incididunt labore</li>
                </ul>
            </div>
            <PanelImage className="hs2-panel-image" alt="Amet consec" />
        </div>
    )
}

/* Tab 3 — Image left, large heading + text right */
function PanelContent() {
    return (
        <div className="hs2-panel hs2-panel-split hs2-panel-reverse">
            <PanelImage className="hs2-panel-image" alt="Adipiscing" />
            <div className="hs2-panel-col" style={{ gap: "0.5rem", justifyContent: "center" }}>
                <span className="hs2-panel-badge">Adipiscing</span>
                <h3 className="hs2-panel-heading hs2-panel-heading-lg">Consectetur adipiscing elit</h3>
                <p className="hs2-panel-text">
                    Quis nostrud exercitation ullamco laboris nisi ut aliquip
                    ex ea commodo consequat duis aute irure dolor.
                </p>
            </div>
        </div>
    )
}

/* Tab 4 — Two stacked images left, text right */
function PanelGallery() {
    return (
        <div className="hs2-panel hs2-panel-split">
            <div className="hs2-panel-duo">
                <PanelImage className="hs2-panel-duo-img" alt="Elit sed 1" />
                <PanelImage className="hs2-panel-duo-img" alt="Elit sed 2" />
            </div>
            <div className="hs2-panel-col" style={{ gap: "0.625rem", justifyContent: "center" }}>
                <span className="hs2-panel-badge">Elit</span>
                <h3 className="hs2-panel-heading">Elit sed do eiusmod tempor</h3>
                <p className="hs2-panel-text">
                    Excepteur sint occaecat cupidatat non proident, sunt in culpa
                    qui officia deserunt mollit anim id est laborum.
                </p>
            </div>
        </div>
    )
}

const panels = [PanelHero, PanelDashboard, PanelMedia, PanelContent, PanelGallery]

export function HeroSectionV2({
    className,
    ...props
}: React.ComponentProps<"div">) {
    const [activeTab, setActiveTab] = useState(0)

    const ActivePanel = panels[activeTab]

    return (
        <div className={cn("hs2-root", className)} {...props}>
            <div className="hs2-inner">
                {/* Hero Top — text row (title + description), then buttons */}
                <div className="hs2-hero-top">
                    <div className="hs2-hero-text">
                        <h1 className="hs2-headline">
                            Lorem ipsum dolor sit amet
                        </h1>
                        <p className="hs2-description">
                            Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed
                            do eiusmod tempor incididunt ut labore et dolore magna aliqua.
                            Ut enim ad minim veniam, quis nostrud exercitation ullamco.
                        </p>
                    </div>
                    <div className="hs2-actions">
                        <Button
                            variant="default"
                            className="rounded-full hs2-btn-primary"
                        >
                            Sign up
                        </Button>
                        <Button
                            variant="outline"
                            className="rounded-full hs2-btn-secondary"
                        >
                            Contact sales
                        </Button>
                    </div>
                </div>

                {/* Interactive Card */}
                <div className="hs2-card">
                    {/* Tab Navigation */}
                    <div className="hs2-tabs">
                        {tabs.map((tab, i) => (
                            <button
                                key={tab}
                                className={cn(
                                    "hs2-tab",
                                    activeTab === i && "hs2-tab-active"
                                )}
                                onClick={() => setActiveTab(i)}
                            >
                                {tab}
                            </button>
                        ))}
                    </div>

                    {/* Card Content — image panels per tab */}
                    <div className="hs2-card-content">
                        <ActivePanel />
                    </div>
                </div>
            </div>
        </div>
    )
}

/* ============================================
   Hero Section V2 — Clean, modern, serif display heading,
   pill buttons, warm interactive card,
   tabbed image content
   ============================================ */

/* ── Root ────────────────────────────────────── */

.hs2-root {
  width: 100%;
  position: relative;
  background: var(--background);
}

/* ── Inner wrapper ───────────────────────────── */

.hs2-inner {
  position: relative;
  z-index: 10;
  display: flex;
  flex-direction: column;
  max-width: 72rem;
  margin-inline: auto;
  width: 100%;
  padding: 4rem 1.25rem 0;
}

@media (min-width: 768px) {
  .hs2-inner {
    padding: 5rem 2rem 0;
  }
}

@media (min-width: 1024px) {
  .hs2-inner {
    padding: 6rem 3rem 0;
  }
}

/* ── Hero top — vertical stack: text row, then buttons ── */

.hs2-hero-top {
  display: flex;
  flex-direction: column;
  gap: 1.25rem;
  margin-bottom: 2rem;
}

@media (min-width: 768px) {
  .hs2-hero-top {
    gap: 1.75rem;
    margin-bottom: 3rem;
  }
}

@media (min-width: 1024px) {
  .hs2-hero-top {
    gap: 2rem;
    margin-bottom: 3.5rem;
  }
}

/* ── Text row — title left, description right, top-aligned ── */

.hs2-hero-text {
  display: grid;
  grid-template-columns: 1fr;
  gap: 0.75rem;
  align-items: start;
}

@media (min-width: 768px) {
  .hs2-hero-text {
    gap: 1.5rem;
  }
}

@media (min-width: 1024px) {
  .hs2-hero-text {
    grid-template-columns: 1fr 1fr;
    gap: 3rem;
  }
}

/* ── Heading ─────────────────────────────────── */

.hs2-headline {
  font-family: var(--font-display);
  font-size: clamp(2rem, 4.5vw, 3.25rem);
  font-weight: 400;
  line-height: 1.1;
  letter-spacing: -0.015em;
  color: var(--foreground);
  margin: 0;
}

/* ── Description ─────────────────────────────── */

.hs2-description {
  font-family: var(--font-sans);
  font-size: 0.875rem;
  line-height: 1.65;
  color: var(--muted-foreground);
  max-width: 32rem;
  margin: 0;
}

@media (min-width: 480px) {
  .hs2-description {
    font-size: 0.9375rem;
    line-height: 1.7;
  }
}

@media (min-width: 768px) {
  .hs2-description {
    font-size: 1rem;
  }
}

/* ── CTA actions (below text row) ────────────── */

.hs2-actions {
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  gap: 0.75rem;
}

.hs2-btn-primary {
  font-weight: 500;
  padding-inline: 1.75rem;
}

.hs2-btn-secondary {
  font-weight: 500;
  padding-inline: 1.5rem;
}

/* ── Interactive card ────────────────────────── */

.hs2-card {
  background: var(--hs2-card-bg);
  border-radius: 1rem;
  padding: 1rem;
  display: flex;
  flex-direction: column;
  gap: 1rem;
  position: relative;
  isolation: isolate;
}

.hs2-card::before {
  content: "";
  position: absolute;
  inset: 0;
  border-radius: inherit;
  pointer-events: none;
  z-index: 1;
  opacity: 0.45;
  filter: url(#hs2-grain);
  background: url("data:image/svg+xml,%3Csvg viewBox='0 0 256 256' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='hs2-grain'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.75' numOctaves='4' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23hs2-grain)'/%3E%3C/svg%3E");
  background-size: 180px 180px;
  mix-blend-mode: overlay;
}

.dark .hs2-card::before {
  opacity: 0.3;
  mix-blend-mode: soft-light;
}

:root {
  --hs2-card-bg: oklch(0.95 0.01 75 / 1);
  --hs2-subcard-bg: var(--background);
  --hs2-subcard-border: var(--border);
}

.dark {
  --hs2-card-bg: oklch(0.22 0.01 75 / 1);
  --hs2-subcard-bg: oklch(0.18 0.005 75 / 1);
  --hs2-subcard-border: oklch(0.30 0.01 75 / 1);
}

@media (min-width: 480px) {
  .hs2-card {
    border-radius: 1.125rem;
    padding: 1.25rem;
    gap: 1.25rem;
  }
}

@media (min-width: 768px) {
  .hs2-card {
    border-radius: 1.25rem;
    padding: 1.5rem;
    gap: 1.5rem;
  }
}

@media (min-width: 1024px) {
  .hs2-card {
    padding: 2rem;
  }
}

/* ── Tab navigation ──────────────────────────── */

.hs2-tabs {
  display: flex;
  align-items: center;
  gap: 0.375rem;
  justify-content: flex-start;
  overflow-x: auto;
  -webkit-overflow-scrolling: touch;
  scrollbar-width: none;
  -ms-overflow-style: none;
}

.hs2-tabs::-webkit-scrollbar {
  display: none;
}

@media (min-width: 768px) {
  .hs2-tabs {
    justify-content: center;
    overflow-x: visible;
    flex-wrap: wrap;
  }
}

.hs2-tab {
  font-family: var(--font-sans);
  font-size: 0.8125rem;
  font-weight: 500;
  color: var(--muted-foreground);
  background: transparent;
  border: 1px solid transparent;
  border-radius: 9999px;
  padding: 0.4375rem 0.875rem;
  cursor: pointer;
  transition: all 0.2s ease;
  white-space: nowrap;
  line-height: 1.4;
  flex-shrink: 0;
}

.hs2-tab:hover {
  color: var(--foreground);
  background: var(--hs2-subcard-bg);
}

.hs2-tab-active {
  color: var(--foreground);
  background: var(--hs2-subcard-bg);
  border-color: var(--hs2-subcard-border);
  box-shadow: 0 1px 2px oklch(0 0 0 / 0.04);
}

@media (min-width: 768px) {
  .hs2-tab {
    font-size: 0.875rem;
    padding: 0.5rem 1.125rem;
  }
}

/* ── Card content wrapper — responsive height, centered ── */

.hs2-card-content {
  background: var(--hs2-subcard-bg);
  border: 1px solid var(--hs2-subcard-border);
  border-radius: 0.875rem;
  padding: 1rem;
  height: 16rem;
  overflow: hidden;
  max-width: 52rem;
  margin-inline: auto;
  width: 100%;
}

@media (min-width: 480px) {
  .hs2-card-content {
    padding: 1.25rem;
    height: 18rem;
  }
}

@media (min-width: 768px) {
  .hs2-card-content {
    padding: 1.5rem;
    height: 20rem;
  }
}

@media (min-width: 1024px) {
  .hs2-card-content {
    padding: 2rem;
    height: 22rem;
  }
}

/* ── Panel base ──────────────────────────────── */

.hs2-panel {
  display: flex;
  flex-direction: column;
  width: 100%;
  height: 100%;
}

/* Split — two columns (text + image) */
.hs2-panel-split {
  gap: 1rem;
}

@media (min-width: 768px) {
  .hs2-panel-split {
    flex-direction: row;
    align-items: stretch;
    gap: 1.25rem;
  }
}

/* Reverse — image left, text right on desktop */
@media (min-width: 768px) {
  .hs2-panel-reverse {
    flex-direction: row;
  }
}

/* Bullet list inside panels */
.hs2-panel-list {
  list-style: none;
  padding: 0;
  margin: 0;
  display: flex;
  flex-direction: column;
  gap: 0.375rem;
}

.hs2-panel-list li {
  font-family: var(--font-sans);
  font-size: 0.8125rem;
  line-height: 1.55;
  color: var(--muted-foreground);
  padding-left: 1rem;
  position: relative;
}

.hs2-panel-list li::before {
  content: "";
  position: absolute;
  left: 0;
  top: 0.5em;
  width: 5px;
  height: 5px;
  border-radius: 50%;
  background: var(--muted-foreground);
  opacity: 0.45;
}

/* Large heading variant */
.hs2-panel-heading-lg {
  font-size: 1.25rem;
  letter-spacing: -0.02em;
}

@media (min-width: 768px) {
  .hs2-panel-heading-lg {
    font-size: 1.5rem;
  }
}

/* Duo — two stacked images in a column */
.hs2-panel-duo {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
  flex: 1;
  min-width: 0;
  min-height: 0;
}

.hs2-panel-duo-img {
  flex: 1;
  min-height: 0;
  border-radius: 0.375rem;
}

@media (max-width: 767px) {
  .hs2-panel-duo {
    display: none;
  }
}

/* Stack — vertical flow */
.hs2-panel-stack {
  gap: 0.625rem;
}

/* Column inside split panels */
.hs2-panel-col {
  display: flex;
  flex-direction: column;
  flex: 1;
  min-width: 0;
}

/* Image placeholder inside split panels */
.hs2-panel-image {
  width: 100%;
  flex-shrink: 0;
  flex: 1;
  min-height: 0;
}

@media (max-width: 767px) {
  .hs2-panel-image {
    display: none;
  }
}

/* ── Image wrapper ────────────────────────────── */

.hs2-img-wrap {
  overflow: hidden;
  border-radius: 0.5rem;
  position: relative;
  background: var(--muted);
}

.hs2-img-overlay {
  position: absolute;
  inset: 0;
  z-index: 2;
  background: oklch(0.55 0.06 75);
  opacity: 0.45;
  mix-blend-mode: color;
}

.dark .hs2-img-overlay {
  background: oklch(0.45 0.05 75);
  opacity: 0.5;
}

.hs2-img {
  position: relative;
  z-index: 1;
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
  filter: brightness(0.85) saturate(0.7);
}


/* ── Panel content styles ────────────────────── */

.hs2-panel-badge {
  font-family: var(--font-sans);
  font-size: 0.6875rem;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.06em;
  color: var(--muted-foreground);
}

.hs2-panel-heading {
  font-family: var(--font-sans);
  font-size: 1rem;
  font-weight: 600;
  line-height: 1.3;
  color: var(--foreground);
  margin: 0;
}

.hs2-panel-text {
  font-family: var(--font-sans);
  font-size: 0.8125rem;
  line-height: 1.55;
  color: var(--muted-foreground);
  margin: 0;
}


/* ── Entry animations — staggered, gentle ────── */

@keyframes hs2-fade-up {
  from {
    opacity: 0;
    transform: translateY(18px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.hs2-hero-top {
  animation: hs2-fade-up 0.6s cubic-bezier(0.22, 1, 0.36, 1) 0.05s both;
}

.hs2-actions {
  animation: hs2-fade-up 0.55s cubic-bezier(0.22, 1, 0.36, 1) 0.15s both;
}

.hs2-card {
  animation: hs2-fade-up 0.65s cubic-bezier(0.22, 1, 0.36, 1) 0.2s both;
}

/* ── Reduced motion ──────────────────────────── */

@media (prefers-reduced-motion: reduce) {
  .hs2-hero-top,
  .hs2-actions,
  .hs2-card {
    animation: none;
  }
}
