import { HeroSectionV3 } from "../hero-section-v3"

export default function Page() {
    return <HeroSectionV3 />
}

/* ─── Hero Section V3 ─────────────────────────── */
/* Centered hero with feature grid · Clean minimal  */
/* Supports light and dark mode via oklch            */

/* ─── Theme Variables (Light) ───────────────── */

:root {
  --hs3-bg: var(--background);
  --hs3-card-bg: oklch(0.97 0 0);
  --hs3-text: oklch(0.145 0 0);
  --hs3-text-muted: oklch(0.45 0 0);
  --hs3-border: oklch(0.922 0 0);
}

/* ─── Theme Variables (Dark) ────────────────── */

.dark {
  --hs3-bg: var(--background);
  --hs3-card-bg: oklch(0.16 0 0);
  --hs3-text: oklch(0.985 0 0);
  --hs3-text-muted: oklch(0.6 0 0);
  --hs3-border: oklch(0.25 0 0);
}

/* ─── Section Root ───────────────────────────── */

.hs3-section {
  width: 100%;
  background: var(--hs3-bg);
  color: var(--hs3-text);
  font-family: var(--font-sans);
}

.hs3-root {
  width: 100%;
  max-width: 72rem;
  margin: 0 auto;
  padding: 4rem 1.25rem 3rem;
}

/* ─── Header ────────────────────────────────── */

.hs3-header {
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  gap: 1.25rem;
  padding-bottom: 3.5rem;
  animation: hs3-fade-up 0.6s cubic-bezier(0.22, 1, 0.36, 1) both;
}

/* ─── Header ────────────────────────────────── */

.hs3-heading {
  font-family: var(--font-sans);
  font-size: clamp(1.875rem, 5vw, 3.5rem);
  font-weight: 400;
  line-height: 1.1;
  letter-spacing: -0.02em;
  color: var(--hs3-text);
  max-width: 48rem;
  margin: 0;
  text-wrap: balance;
}

/* ─── Description ────────────────────────────── */

.hs3-description {
  font-size: clamp(0.875rem, 1vw + 0.4rem, 1.0625rem);
  line-height: 1.65;
  letter-spacing: -0.01em;
  color: var(--hs3-text-muted);
  max-width: 32rem;
  margin: 0;
  text-wrap: balance;
}

/* ─── Feature Grid ───────────────────────────── */

.hs3-grid {
  display: grid;
  grid-template-columns: 1fr;
  border: 1px solid var(--hs3-border);
  animation: hs3-fade-up 0.7s cubic-bezier(0.22, 1, 0.36, 1) 0.1s both;
}

.hs3-card {
  padding: 1.25rem 1rem;
  background: var(--hs3-card-bg);
  border-bottom: 1px solid var(--hs3-border);
}

.hs3-card:last-child {
  border-bottom: none;
}

/* ─── Card Content ───────────────────────────── */

.hs3-card-icon-wrap {
  width: 2.5rem;
  height: 2.5rem;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 0.875rem;
}

.hs3-card-icon {
  width: 1.125rem;
  height: 1.125rem;
  color: var(--hs3-text-muted);
}

.hs3-card-title {
  font-size: 0.9375rem;
  font-weight: 600;
  line-height: 1.4;
  color: var(--hs3-text);
  margin: 0 0 0.5rem;
}

.hs3-card-description {
  font-size: 0.875rem;
  line-height: 1.6;
  color: var(--hs3-text-muted);
  margin: 0;
}

/* ─── Animations ─────────────────────────────── */

@keyframes hs3-fade-up {
  from {
    opacity: 0;
    transform: translateY(18px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* ─── Responsive: sm ─────────────────────────── */

@media (min-width: 640px) {
  .hs3-root {
    padding: 5rem 2rem 3.5rem;
  }

  .hs3-grid {
    grid-template-columns: repeat(2, 1fr);
  }

  .hs3-card {
    padding: 1.5rem;
  }

  .hs3-card:nth-child(odd) {
    border-right: 1px solid var(--hs3-border);
  }

  .hs3-card:last-child,
  .hs3-card:nth-last-child(2):nth-child(odd) {
    border-bottom: none;
  }
}

/* ─── Responsive: md ─────────────────────────── */

@media (min-width: 768px) {
  .hs3-root {
    padding: 5rem 2.5rem 4rem;
  }

  .hs3-header {
    padding-bottom: 4rem;
  }
}

/* ─── Responsive: lg ─────────────────────────── */

@media (min-width: 1024px) {
  .hs3-root {
    padding: 6rem 3rem 4rem;
  }

  .hs3-grid {
    grid-template-columns: repeat(3, 1fr);
  }

  .hs3-card {
    padding: 1.5rem 1.75rem;
  }

  .hs3-card:nth-child(odd) {
    border-right: none;
  }

  .hs3-card:nth-last-child(2):nth-child(odd) {
    border-bottom: revert;
  }

  .hs3-card:not(:nth-child(3n)) {
    border-right: 1px solid var(--hs3-border);
  }

  .hs3-card:nth-child(n + 4) {
    border-bottom: none;
  }
}

/* ─── Responsive: xl ─────────────────────────── */

@media (min-width: 1280px) {
  .hs3-root {
    padding: 7rem 4rem 5rem;
  }

  .hs3-header {
    gap: 1.5rem;
    padding-bottom: 4.5rem;
  }
}

/* ─── Reduced Motion ─────────────────────────── */

@media (prefers-reduced-motion: reduce) {
  .hs3-header,
  .hs3-grid {
    animation: none;
  }
}
