import { HeroSectionV3 } from "../hero-section-v3"

export default function Page() {
    return <HeroSectionV3 />
}

/* ─── Hero Section V3 ─────────────────────────── */
/* Clean minimal hero with lime green accent       */
/* Supports light and dark mode via oklch           */

/* ─── Theme Variables (Light) ───────────────── */

:root {
  --hs3-bg: oklch(1 0 0);
  --hs3-text: oklch(0.145 0 0);
  --hs3-text-muted: oklch(0.48 0 0);
  --hs3-border: oklch(0.922 0 0);
  --hs3-border-hover: oklch(0.85 0 0);
  --hs3-announce-bg: oklch(0.975 0 0);
  --hs3-announce-text: oklch(0.48 0 0);
  --hs3-announce-hover: oklch(0.145 0 0);
  --hs3-badge-bg: oklch(0.94 0.06 128.85);
  --hs3-badge-text: oklch(0.42 0.16 128.85);
  --hs3-cta-bg: oklch(0.841 0.238 128.85);
  --hs3-cta-bg-hover: oklch(0.78 0.22 128.85);
  --hs3-cta-text: oklch(0.145 0 0);
  --hs3-cta-shadow: oklch(0.841 0.238 128.85 / 0.18);
  --hs3-cta-shadow-hover: oklch(0.841 0.238 128.85 / 0.32);
}

/* ─── Theme Variables (Dark) ────────────────── */

.dark {
  --hs3-bg: oklch(0.145 0 0);
  --hs3-text: oklch(0.985 0 0);
  --hs3-text-muted: oklch(0.62 0 0);
  --hs3-border: oklch(0.275 0 0);
  --hs3-border-hover: oklch(0.38 0 0);
  --hs3-announce-bg: oklch(0.19 0 0);
  --hs3-announce-text: oklch(0.62 0 0);
  --hs3-announce-hover: oklch(0.985 0 0);
  --hs3-badge-bg: oklch(0.28 0.07 128.85);
  --hs3-badge-text: oklch(0.841 0.238 128.85);
  --hs3-cta-bg: oklch(0.841 0.238 128.85);
  --hs3-cta-bg-hover: oklch(0.88 0.25 128.85);
  --hs3-cta-text: oklch(0.145 0 0);
  --hs3-cta-shadow: oklch(0.841 0.238 128.85 / 0.14);
  --hs3-cta-shadow-hover: oklch(0.841 0.238 128.85 / 0.28);
}

/* ─── Section Root ───────────────────────────── */

.hs3-section {
  --hs3-display: var(--font-display, "Instrument Serif", serif);
  --hs3-sans: var(--font-sans, "Geist", sans-serif);

  width: 100%;
  overflow: hidden;
  background: var(--hs3-bg);
  color: var(--hs3-text);
}

.hs3-root {
  position: relative;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
}

/* ─── Content Layer ──────────────────────────── */

.hs3-content {
  position: relative;
  z-index: 10;
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  padding: 1.5rem;
  max-width: 52rem;
  width: 100%;
}

/* ─── Announcement Bar ───────────────────────── */

.hs3-announce {
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.35rem 0.85rem 0.35rem 0.4rem;
  border: 1px solid var(--hs3-border);
  border-radius: 9999px;
  font-size: 0.8125rem;
  line-height: 1.4;
  color: var(--hs3-announce-text);
  text-decoration: none;
  background: var(--hs3-announce-bg);
  transition: border-color 0.2s ease, color 0.2s ease;
}

.hs3-announce:hover {
  border-color: var(--hs3-border-hover);
  color: var(--hs3-announce-hover);
}

.hs3-announce-badge {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  padding: 0.125rem 0.5rem;
  border-radius: 9999px;
  background: var(--hs3-badge-bg);
  color: var(--hs3-badge-text);
  font-size: 0.6875rem;
  font-weight: 600;
  letter-spacing: 0.03em;
  text-transform: uppercase;
}

.hs3-announce-text {
  white-space: nowrap;
}

.hs3-announce-icon {
  width: 0.875rem;
  height: 0.875rem;
  opacity: 0.5;
  flex-shrink: 0;
}

/* ─── Heading ────────────────────────────────── */

.hs3-heading {
  font-family: var(--hs3-sans);
  font-size: clamp(2.25rem, 5.5vw, 4rem);
  font-weight: 600;
  line-height: 1.08;
  letter-spacing: -0.03em;
  color: var(--hs3-text);
  margin: 1.75rem 0 0;
}

/* ─── Description ────────────────────────────── */

.hs3-description {
  font-size: clamp(1rem, 1.5vw, 1.125rem);
  line-height: 1.7;
  letter-spacing: 0.005em;
  color: var(--hs3-text-muted);
  max-width: 34rem;
  margin: 1.25rem 0 0;
  text-wrap: balance;
}

.hs3-br-desktop {
  display: none;
}

/* ─── CTA Button ─────────────────────────────── */

.hs3-cta {
  margin-top: 2rem;
  background: var(--hs3-cta-bg);
  color: var(--hs3-cta-text);
  border: none;
  border-radius: 9999px;
  font-weight: 600;
  font-size: 0.9375rem;
  padding: 0.65rem 1.75rem;
  cursor: pointer;
  box-shadow: 0 2px 24px var(--hs3-cta-shadow);
  transition: background 0.2s ease, transform 0.15s ease, box-shadow 0.2s ease;
}

.hs3-cta:hover {
  background: var(--hs3-cta-bg-hover);
  transform: translateY(-1px);
  box-shadow: 0 4px 32px var(--hs3-cta-shadow-hover);
}

.hs3-cta:active {
  transform: translateY(0);
}

/* ─── Responsive ─────────────────────────────── */

@media (min-width: 640px) {
  .hs3-content {
    padding: 2.5rem;
  }

  .hs3-heading {
    margin-top: 2rem;
  }

  .hs3-description {
    margin-top: 1.5rem;
  }

  .hs3-cta {
    margin-top: 2.25rem;
  }

  .hs3-br-desktop {
    display: inline;
  }
}

@media (min-width: 1024px) {
  .hs3-heading {
    margin-top: 2.25rem;
  }

  .hs3-description {
    margin-top: 1.5rem;
  }

  .hs3-cta {
    margin-top: 2.5rem;
  }
}

/* ─── Reduced Motion ─────────────────────────── */

@media (prefers-reduced-motion: reduce) {
  .hs3-cta,
  .hs3-announce {
    transition: none;
  }
}
