import { HeroSectionV4 } from "../hero-section-v4"

export default function Page() {
    return <HeroSectionV4 />
}

"use client"

import { useEffect, useState } from "react"
import { cn } from "@/lib/utils"
import { Dithering } from "@paper-design/shaders-react"

function getShaderColors() {
  const s = getComputedStyle(document.documentElement)
  return {
    colorBack: s.getPropertyValue("--hs4-shader-back").trim() || "#fcfcfc",
    colorFront: s.getPropertyValue("--hs4-shader-front").trim() || "#0284c7",
  }
}

export function HeroSectionV4Dither({ className }: { className?: string }) {
  const [mounted, setMounted] = useState(false)
  const [palette, setPalette] = useState({
    colorBack: "#fcfcfc",
    colorFront: "#0284c7",
  })

  useEffect(() => {
    setMounted(true)
    setPalette(getShaderColors())

    const observer = new MutationObserver(() => setPalette(getShaderColors()))
    observer.observe(document.documentElement, {
      attributes: true,
      attributeFilter: ["class"],
    })
    return () => observer.disconnect()
  }, [])

  if (!mounted) return <div className={cn("hs4-dither-placeholder", className)} />

  return (
    <Dithering
      width={640}
      height={640}
      colorBack={palette.colorBack}
      colorFront={palette.colorFront}
      shape="sphere"
      type="4x4"
      size={2}
      speed={1}
      scale={0.6}
      className={cn("hs4-dither-shader", className)}
    />
  )
}

/* ─── Hero Section V4 ─────────────────────────── */
/* Blue theme · Split layout · Dithering circle    */
/* Full light & dark mode support                  */

/* ─── Theme Variables (Light) ───────────────── */

:root {
  --hs4-bg: var(--background);
  --hs4-text: #0c1929;
  --hs4-text-muted: rgba(12, 25, 41, 0.55);
  --hs4-accent: #0284c7;
  --hs4-accent-bright: #0ea5e9;
  --hs4-heading-accent-from: #0284c7;
  --hs4-heading-accent-to: #38bdf8;
  --hs4-shader-front: #0284c7;
  --hs4-shader-back: #fcfcfc;
}

/* ─── Theme Variables (Dark) ────────────────── */

.dark {
  --hs4-bg: var(--background);
  --hs4-text: #e8f0fa;
  --hs4-text-muted: rgba(232, 240, 250, 0.5);
  --hs4-accent: #38bdf8;
  --hs4-accent-bright: #7dd3fc;
  --hs4-heading-accent-from: #38bdf8;
  --hs4-heading-accent-to: #7dd3fc;
  --hs4-shader-front: #00b3ff;
  --hs4-shader-back: #000000;
}

/* ─── Section Root ───────────────────────────── */

.hs4-section {
  --hs4-display: var(--font-display, "Instrument Serif", serif);
  --hs4-sans: var(--font-sans, "Geist", sans-serif);

  width: 100%;
  overflow: hidden;
  background: var(--hs4-bg);
  color: var(--hs4-text);
  font-family: var(--font-sans);
}

.hs4-root {
  position: relative;
  width: 100%;
  display: flex;
  align-items: flex-start;
  justify-content: center;
  padding: 1.25rem;
}

/* ─── Content Layer ─────────────────────────── */

.hs4-content {
  position: relative;
  z-index: 10;
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  gap: 0;
  padding: 3.5rem 1.25rem 0;
  width: 100%;
  max-width: 76rem;
  margin: 0 auto;
}

/* ─── Split Layout ──────────────────────────── */

.hs4-split {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 2.5rem;
  width: 100%;
}

/* ─── Left: Text Column ─────────────────────── */

.hs4-text-col {
  display: flex;
  flex-direction: column;
  gap: 1.25rem;
  text-align: center;
  align-items: center;
  flex: 1;
  min-width: 0;
  max-width: 36rem;
}

/* ─── Right: Visual Column ──────────────────── */

.hs4-visual-col {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
}

/* ─── Dither Circle ─────────────────────────── */

.hs4-dither-circle {
  width: 280px;
  height: 280px;
  max-width: 100%;
  aspect-ratio: 1;
}

.hs4-dither-shader {
  width: 100%;
  height: 100%;
  display: block;
}

.hs4-dither-placeholder {
  width: 100%;
  height: 100%;
}

/* ─── Heading ───────────────────────────────── */

.hs4-heading {
  font-family: var(--hs4-sans);
  font-size: clamp(1.875rem, 3.6vw, 2.75rem);
  font-weight: 600;
  line-height: 1.15;
  letter-spacing: -0.03em;
  color: var(--hs4-text);
  margin: 0;
}

.hs4-heading-accent {
  background: linear-gradient(
    135deg,
    var(--hs4-heading-accent-from),
    var(--hs4-heading-accent-to)
  );
  -webkit-background-clip: text;
  background-clip: text;
  -webkit-text-fill-color: transparent;
  padding: 0.05em 0.05em;
}

/* ─── Description ───────────────────────────── */

.hs4-description {
  font-size: clamp(0.9375rem, 1.1vw + 0.5rem, 1.0625rem);
  line-height: 1.65;
  letter-spacing: -0.01em;
  color: var(--hs4-text-muted);
  max-width: min(30rem, 100%);
  margin: 0;
  text-wrap: balance;
}

/* ─── CTA Group ─────────────────────────────── */

.hs4-cta-group {
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
  gap: 0.75rem;
}

/* ─── Responsive ────────────────────────────── */

@media (min-width: 480px) {
  .hs4-dither-circle {
    width: 340px;
    height: 340px;
  }
}

@media (min-width: 640px) {
  .hs4-root {
    padding: 1.5rem;
  }

  .hs4-content {
    padding: 4rem 2rem 0;
    gap: 0;
  }

  .hs4-split {
    gap: 3rem;
  }

  .hs4-dither-circle {
    width: 380px;
    height: 380px;
  }
}

@media (min-width: 768px) {
  .hs4-root {
    padding: 2rem;
  }

  .hs4-content {
    padding: 4.5rem 2.5rem 0;
  }

  .hs4-dither-circle {
    width: 420px;
    height: 420px;
  }
}

@media (min-width: 1024px) {
  .hs4-root {
    padding: 2.5rem;
  }

  .hs4-content {
    padding: 5rem 3rem 0;
    align-items: flex-start;
    text-align: left;
  }

  .hs4-split {
    flex-direction: row;
    align-items: center;
    gap: 3rem;
  }

  .hs4-text-col {
    text-align: left;
    align-items: flex-start;
    gap: 1.5rem;
    flex: 1.1;
  }

  .hs4-visual-col {
    flex: 1;
    flex-shrink: 0;
    width: auto;
    justify-content: flex-end;
  }

  .hs4-cta-group {
    justify-content: flex-start;
  }

  .hs4-dither-circle {
    width: 420px;
    height: 420px;
  }
}

@media (min-width: 1280px) {
  .hs4-root {
    padding: 3rem;
  }

  .hs4-content {
    padding: 5rem 4rem 0;
  }

  .hs4-split {
    gap: 4rem;
  }

  .hs4-text-col {
    flex: 1.15;
  }

  .hs4-visual-col {
    flex: 1;
  }

  .hs4-dither-circle {
    width: 480px;
    height: 480px;
  }
}

@media (min-width: 1440px) {
  .hs4-split {
    gap: 5rem;
  }

  .hs4-dither-circle {
    width: 540px;
    height: 540px;
  }
}
