import { HeroSectionV4 } from "../hero-section-v4"

export default function Page() {
    return <HeroSectionV4 />
}

import { useEffect, useState } from "react"
import "./hero-section-v4.css"
import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"
import { Badge } from "@/components/ui/badge"
import { ArrowRight } from "lucide-react"
import { Dithering } from "@paper-design/shaders-react"

/* ─── Read shader colours from CSS variables ─── */

function getShaderColors() {
  const s = getComputedStyle(document.documentElement)
  return {
    colorBack: s.getPropertyValue("--hs4-shader-back").trim(),
    colorFront: s.getPropertyValue("--hs4-shader-front").trim(),
  }
}

/* ─── Main Component ─────────────────────────── */

export function HeroSectionV4({
  className,
  ...props
}: React.ComponentProps<"section">) {
  const [mounted, setMounted] = useState(false)
  const [palette, setPalette] = useState({ colorBack: "#ffffff", colorFront: "#0284c7" })

  useEffect(() => {
    setMounted(true)
    setPalette(getShaderColors())

    /* Re-read CSS vars when theme class toggles */
    const observer = new MutationObserver(() => setPalette(getShaderColors()))
    observer.observe(document.documentElement, {
      attributes: true,
      attributeFilter: ["class"],
    })
    return () => observer.disconnect()
  }, [])

  return (
    <section className={cn("hs4-section", className)} {...props}>
      <div className="hs4-root min-h-screen">
        {/* Content */}
        <div className="hs4-content">
          {/* Announcement */}
          <Badge variant="secondary">
            Announcing Our Latest Release
            <ArrowRight />
          </Badge>

          {/* Split Layout */}
          <div className="hs4-split">
            {/* Left: Text + CTA */}
            <div className="hs4-text-col">
              <h1 className="hs4-heading">
                Lorem ipsum dolor
                <br />
                sit amet{" "}
                <span className="hs4-heading-accent">consectetur</span>
              </h1>
              <p className="hs4-description">
                Sed do eiusmod tempor incididunt ut labore et dolore magna
                aliqua. Ut enim ad minim veniam, quis nostrud exercitation.
              </p>
              <div className="hs4-cta-group">
                <Button size="default" variant="default"
                >
                  Get started free
                </Button>
                <Button size="default" variant="outline"
                >
                  See it in action
                </Button>
              </div>
            </div>

            {/* Right: Dither Circle */}
            <div className="hs4-visual-col">
              <div className="hs4-dither-circle">
                {mounted && (
                  <Dithering
                    width={640}
                    height={640}
                    colorBack={palette.colorBack}
                    colorFront={palette.colorFront}
                    shape="sphere"
                    type="4x4"
                    size={2}
                    speed={1}
                    scale={0.6}
                    className="hs4-dither-shader"
                  />
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  )
}

/* ─── Hero Section V4 ─────────────────────────── */
/* Blue theme · Split layout · Dithering circle    */
/* Full light & dark mode support                  */

/* ─── Theme Variables (Light) ───────────────── */

:root {
  --hs4-bg: #ffffff;
  --hs4-text: #0c1929;
  --hs4-text-muted: rgba(12, 25, 41, 0.55);
  --hs4-accent: #0284c7;
  --hs4-accent-bright: #0ea5e9;
  --hs4-heading-accent-from: #0284c7;
  --hs4-heading-accent-to: #38bdf8;
  --hs4-shader-back: #ffffff;
  --hs4-shader-front: #0284c7;
}

/* ─── Theme Variables (Dark) ────────────────── */

.dark {
  --hs4-bg: #09090b;
  --hs4-text: #e8f0fa;
  --hs4-text-muted: rgba(232, 240, 250, 0.5);
  --hs4-accent: #38bdf8;
  --hs4-accent-bright: #7dd3fc;
  --hs4-heading-accent-from: #38bdf8;
  --hs4-heading-accent-to: #7dd3fc;
  --hs4-shader-back: #09090b;
  --hs4-shader-front: #00b3ff;
}

/* ─── Section Root ───────────────────────────── */

.hs4-section {
  --hs4-display: var(--font-display, "Instrument Serif", serif);
  --hs4-sans: var(--font-sans, "Geist", sans-serif);

  width: 100%;
  overflow: hidden;
  background: var(--hs4-bg);
  color: var(--hs4-text);
}

.hs4-root {
  position: relative;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
}

/* ─── Content Layer ─────────────────────────── */

.hs4-content {
  position: relative;
  z-index: 10;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1.5rem;
  padding: 5rem 1.5rem 3rem;
  max-width: 76rem;
  width: 100%;
  margin: 0 auto;
}

/* ─── Split Layout ──────────────────────────── */

.hs4-split {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 2rem;
  width: 100%;
}

/* ─── Left: Text Column ─────────────────────── */

.hs4-text-col {
  display: flex;
  flex-direction: column;
  gap: 1.25rem;
  text-align: center;
  align-items: center;
  flex: 1;
  min-width: 0;
}

/* ─── Right: Visual Column ──────────────────── */

.hs4-visual-col {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
}

/* ─── Dither Circle ─────────────────────────── */

.hs4-dither-circle {
  width: 360px;
  height: 360px;
  max-width: 100%;
  aspect-ratio: 1;
}

.hs4-dither-shader {
  width: 100%;
  height: 100%;
  display: block;
}

/* ─── Heading ───────────────────────────────── */

.hs4-heading {
  font-family: var(--hs4-sans);
  font-size: clamp(2rem, 3.8vw, 3rem);
  font-weight: 600;
  line-height: 1.15;
  letter-spacing: -0.03em;
  color: var(--hs4-text);
  margin: 0;
}

.hs4-heading-accent {
  background: linear-gradient(
    135deg,
    var(--hs4-heading-accent-from),
    var(--hs4-heading-accent-to)
  );
  -webkit-background-clip: text;
  background-clip: text;
  -webkit-text-fill-color: transparent;
  padding: 0.05em 0.05em;
}

/* ─── Description ───────────────────────────── */

.hs4-description {
  font-size: clamp(0.9375rem, 1.2vw + 0.5rem, 1.0625rem);
  line-height: 1.6;
  letter-spacing: -0.01em;
  color: var(--hs4-text-muted);
  max-width: min(32rem, 100%);
  margin: 0;
  text-wrap: balance;
}

/* ─── CTA Group ─────────────────────────────── */

.hs4-cta-group {
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  gap: 0.75rem;
  margin-top: 0.5rem;
}

/* ─── Responsive ────────────────────────────── */

@media (min-width: 480px) {
  .hs4-dither-circle {
    width: 400px;
    height: 400px;
  }
}

@media (min-width: 640px) {
  .hs4-content {
    padding: 5rem 2.5rem 3.5rem;
  }

  .hs4-split {
    gap: 2.5rem;
  }

  .hs4-dither-circle {
    width: 480px;
    height: 480px;
  }
}

@media (min-width: 1024px) {
  .hs4-content {
    padding: 0 3rem;
  }

  .hs4-split {
    flex-direction: row;
    align-items: center;
    gap: 4rem;
  }

  .hs4-text-col {
    text-align: left;
    align-items: flex-start;
    gap: 1.5rem;
  }

  .hs4-visual-col {
    flex-shrink: 0;
    width: auto;
  }

  .hs4-dither-circle {
    width: 560px;
    height: 560px;
  }
}

@media (min-width: 1280px) {
  .hs4-content {
    padding: 0 4rem;
  }

  .hs4-split {
    gap: 5rem;
  }

  .hs4-dither-circle {
    width: 640px;
    height: 640px;
  }
}
