import { MacOsTerminal } from "../mac-os-terminal"

export default function Page() {
  return (
    <div className="flex min-h-screen items-center justify-center p-8 bg-background">
      <MacOsTerminal />
    </div>
  )
}

"use client"

import { useEffect, useRef } from "react"
import "./mac-os-terminal.css"
import gsap from "gsap"
import { cn } from "@/lib/utils"

/* ─── Types ──────────────────────────────────── */

export interface TerminalStep {
  type:
  | "command"
  | "prompt"
  | "output"
  | "success"
  | "error"
  | "blank"
  | "loading"
  text: string
  prefix?: string
}

/* ─── Default Content ────────────────────────── */

const DEFAULT_STEPS: TerminalStep[] = [
  {
    type: "command",
    text: 'pnpm dlx shadcn@latest create --preset "https://ui.shadcn.com/init?base=radix&style=nova&baseColor=neutral&theme=neutral&iconLibrary=lucide&font=inter&menuAccent=subtle&menuColor=default&radius=default&template=next&rtl=false" --template next',
  },
  {
    type: "prompt",
    prefix: "√ What is your project named? ",
    text: "hello-world",
  },
  { type: "loading", text: "Creating a new Next.js project..." },
  { type: "success", text: "√ Created a new Next.js project." },
  { type: "success", text: "√ Writing components.json." },
  { type: "success", text: "√ Checking registry." },
  { type: "success", text: "√ Updating CSS variables in app\\globals.css" },
  { type: "success", text: "√ Updating app\\globals.css" },
  { type: "success", text: "√ Installing dependencies." },
  { type: "success", text: "√ Updating fonts." },
  { type: "blank", text: "" },
  { type: "output", text: "√ Created 1 file:" },
  { type: "output", text: "  - lib\\utils.ts" },
  { type: "blank", text: "" },
  { type: "success", text: "Success! Project initialization completed." },
  { type: "output", text: "You may now add components." },
]

/* ─── Component ──────────────────────────────── */

export function MacOsTerminal({
  steps = DEFAULT_STEPS,
  title = "projects",
  subtitle = "-zsh",
  dimensions = "80×20",
  prompt = "lorem@MacBook-Air - %",
  showIcon = true,
  className,
  ...props
}: {
  steps?: TerminalStep[]
  title?: string
  subtitle?: string
  dimensions?: string
  prompt?: string
  showIcon?: boolean
} & React.ComponentProps<"div">) {
  const bodyRef = useRef<HTMLDivElement>(null)
  const cursorRef = useRef<HTMLSpanElement>(null)
  const finalPromptRef = useRef<HTMLDivElement>(null)

  useEffect(() => {
    const body = bodyRef.current
    const cursor = cursorRef.current
    const finalPrompt = finalPromptRef.current
    if (!body || !cursor || !finalPrompt) return

    const lineEls = Array.from(
      body.querySelectorAll<HTMLElement>("[data-line]")
    )
    const typedEls = Array.from(
      body.querySelectorAll<HTMLElement>("[data-typed]")
    )
    const spinnerEls = Array.from(
      body.querySelectorAll<HTMLElement>("[data-spinner]")
    )

    /* Reset state */
    typedEls.forEach((el) => (el.textContent = ""))
    spinnerEls.forEach((el) => (el.textContent = "⠋"))
    gsap.set(lineEls, { opacity: 0, y: 4, display: "flex" })
    gsap.set(finalPrompt, { opacity: 0, y: 4 })
    gsap.set(cursor, { opacity: 0 })

    const tl = gsap.timeline({ delay: 0.6 })

    let typedIdx = 0
    let spinnerIdx = 0

    steps.forEach((step, i) => {
      const lineEl = lineEls[i]
      if (!lineEl) return

      if (step.type === "command" || step.type === "prompt") {
        const typedEl = typedEls[typedIdx++]
        /* Reveal the line */
        tl.to(lineEl, { opacity: 1, y: 0, duration: 0.12 })
        /* Type characters one-by-one */
        if (typedEl) {
          const obj = { chars: 0 }
          tl.to(obj, {
            chars: step.text.length,
            duration: step.text.length * 0.032,
            ease: `steps(${step.text.length})`,
            onUpdate() {
              typedEl.textContent = step.text.slice(0, Math.round(obj.chars))
            },
          })
          if (step.type === "prompt") {
            /* Pause before the success line after user input */
            tl.to({}, { duration: 0.8 })
          } else {
            /* Small pause after command finishes */
            tl.to({}, { duration: 0.35 })
          }
        }
      } else if (step.type === "loading") {
        const spinnerEl = spinnerEls[spinnerIdx++]
        const frames = ["⠋", "⠙", "⠹", "⠸", "⠼", "⠴", "⠦", "⠧", "⠇", "⠏"]
        const cycles = 3

        tl.to(lineEl, { opacity: 1, y: 0, duration: 0.1 })

        if (spinnerEl) {
          const state = { frame: 0 }
          tl.to(state, {
            frame: frames.length * cycles,
            duration: 1.35,
            ease: "none",
            onUpdate() {
              spinnerEl.textContent =
                frames[Math.floor(state.frame) % frames.length]
            },
            onComplete() {
              spinnerEl.textContent = "⠿"
            },
          })
        }
        tl.to(lineEl, { opacity: 0, duration: 0.1 })
        tl.set(lineEl, { display: "none" })
      } else if (step.type === "blank") {
        tl.to(lineEl, { opacity: 1, y: 0, duration: 0.05 })
      } else {
        /* Output / success / error lines fade in quickly */
        tl.to(lineEl, { opacity: 1, y: 0, duration: 0.15 }, ">-0.02")
      }
    })

    /* Reveal final prompt after all terminal steps are complete */
    tl.to(finalPrompt, { opacity: 1, y: 0, duration: 0.12 })

    /* Show blinking cursor */
    tl.to(cursor, { opacity: 1, duration: 0.1 })

    const blink = gsap.to(cursor, {
      opacity: 0,
      duration: 0.53,
      repeat: -1,
      yoyo: true,
      ease: "steps(1)",
      paused: true,
    })
    tl.call(() => { blink.play() })

    return () => {
      tl.kill()
      blink.kill()
    }
  }, [steps])

  return (
    <div className={cn("mot-wrapper", className)} {...props}>
      <div className="mot-terminal">
        {/* Title bar */}
        <div className="mot-titlebar">
          <div className="mot-dots">
            <span className="mot-dot mot-dot-close" />
            <span className="mot-dot mot-dot-minimize" />
            <span className="mot-dot mot-dot-maximize" />
          </div>
          <div className="mot-title-group">
            {showIcon && (
              <svg
                className="mot-title-icon"
                viewBox="0 0 16 16"
                fill="none"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  d="M1.5 5.4C1.5 4.516 2.216 3.8 3.1 3.8H6.2L7.34 5H12.9C13.784 5 14.5 5.716 14.5 6.6V11.9C14.5 12.784 13.784 13.5 12.9 13.5H3.1C2.216 13.5 1.5 12.784 1.5 11.9V5.4Z"
                  fill="currentColor"
                  opacity="0.18"
                />
                <path
                  d="M1.5 5.4C1.5 4.516 2.216 3.8 3.1 3.8H6.2L7.34 5H12.9C13.784 5 14.5 5.716 14.5 6.6V11.9C14.5 12.784 13.784 13.5 12.9 13.5H3.1C2.216 13.5 1.5 12.784 1.5 11.9V5.4Z"
                  stroke="currentColor"
                  strokeWidth="1.1"
                  strokeLinejoin="round"
                />
                <path
                  d="M1.5 6.35H14.5"
                  stroke="currentColor"
                  strokeWidth="1"
                  opacity="0.65"
                />
              </svg>
            )}
            <span className="mot-title">
              {title}
              {subtitle && ` \u2014 ${subtitle}`}
              {dimensions && ` \u2014 ${dimensions}`}
            </span>
          </div>
          <div className="mot-dots-spacer" />
        </div>

        {/* Terminal body */}
        <div className="mot-body" ref={bodyRef}>
          {steps.map((step, i) => (
            <div
              key={i}
              data-line
              className={cn(
                "mot-line",
                step.type === "command" && "mot-line-command",
                step.type === "blank" && "mot-line-blank"
              )}
            >
              {step.type === "command" ? (
                <>
                  <span className="mot-prompt">{prompt}</span>
                  <span data-typed className="mot-typed" />
                </>
              ) : step.type === "prompt" ? (
                <>
                  <span className="mot-text">{step.prefix}</span>
                  <span data-typed className="mot-typed" />
                </>
              ) : step.type === "blank" ? (
                <br />
              ) : step.type === "loading" ? (
                <>
                  <span data-spinner className="mot-spinner">
                    ⠋
                  </span>
                  <span className="mot-text">{step.text}</span>
                </>
              ) : (
                <span
                  className={cn(
                    step.type === "success"
                      ? "mot-text-success"
                      : step.type === "error"
                        ? "mot-text-error"
                        : "mot-text"
                  )}
                >
                  {step.text}
                </span>
              )}
            </div>
          ))}

          {/* Final prompt with blinking cursor */}
          <div ref={finalPromptRef} className="mot-line">
            <span className="mot-prompt">{prompt}</span>
            <span ref={cursorRef} className="mot-cursor">
              ▋
            </span>
          </div>
        </div>
      </div>
    </div>
  )
}

/* ─── macOS Terminal ─────────────────────────────── */
/* Reusable animated macOS terminal component         */

/* ─── Theme Variables ────────────────────────── */

:root {
  /* Title bar – light mode (macOS light chrome) */
  --mot-tb-bg-from: #e8e6e2;
  --mot-tb-bg-to: #dddbd7;
  --mot-tb-border: #c4c2be;
  --mot-tb-title: #4a4846;
  --mot-tb-icon: #2f6feb;

  /* Terminal body – light mode */
  --mot-bg: #ffffff;
  --mot-border: #d4d4d4;
  --mot-shadow: rgba(0, 0, 0, 0.08);
  --mot-shadow-ring: rgba(0, 0, 0, 0.05);
  --mot-prompt: #1a8a6e;
  --mot-typed: #1e1e1e;
  --mot-text: #4b5563;
  --mot-text-success: #16a34a;
  --mot-text-error: #dc2626;
  --mot-cursor: #1e1e1e;
}

.dark {
  /* Title bar – dark mode (macOS dark chrome) */
  --mot-tb-bg-from: #3a3a3c;
  --mot-tb-bg-to: #2d2d2f;
  --mot-tb-border: #1f1f21;
  --mot-tb-title: rgba(255, 255, 255, 0.65);
  --mot-tb-icon: #7aa2ff;

  /* Terminal body – dark mode */
  --mot-bg: #1a1b26;
  --mot-border: rgba(255, 255, 255, 0.08);
  --mot-shadow: rgba(0, 0, 0, 0.5);
  --mot-shadow-ring: rgba(0, 0, 0, 0.3);
  --mot-prompt: #2db8a4;
  --mot-typed: #c0caf5;
  --mot-text: #a9b1d6;
  --mot-text-success: #9ece6a;
  --mot-text-error: #f7768e;
  --mot-cursor: #c0caf5;
}

/* ─── Wrapper ────────────────────────────────── */

.mot-wrapper {
  position: relative;
  width: 100%;
  max-width: 560px;
  margin-left: auto;
  margin-right: auto;
}

/* ─── Terminal Window ────────────────────────── */

.mot-terminal {
  position: relative;
  z-index: 1;
  border-radius: 0.75rem;
  overflow: hidden;
  background: var(--mot-bg);
  border: 1px solid var(--mot-border);
  box-shadow:
    0 0 0 1px var(--mot-shadow-ring),
    0 8px 40px -8px var(--mot-shadow),
    0 2px 12px -2px var(--mot-shadow);
}

/* ─── Title Bar ──────────────────────────────── */

.mot-titlebar {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0.5rem 0.75rem;
  background: linear-gradient(180deg, var(--mot-tb-bg-from) 0%, var(--mot-tb-bg-to) 100%);
  border-bottom: 1px solid var(--mot-tb-border);
  user-select: none;
  position: relative;
  min-height: 2.25rem;
}

.mot-dots {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  z-index: 1;
}

.mot-dot {
  width: 0.75rem;
  height: 0.75rem;
  border-radius: 9999px;
  position: relative;
}

.mot-dot-close {
  background: #ff5f57;
  box-shadow: inset 0 -0.5px 0.5px rgba(0, 0, 0, 0.12);
}

.mot-dot-minimize {
  background: #ffbd2e;
  box-shadow: inset 0 -0.5px 0.5px rgba(0, 0, 0, 0.12);
}

.mot-dot-maximize {
  background: #28c840;
  box-shadow: inset 0 -0.5px 0.5px rgba(0, 0, 0, 0.12);
}

.mot-title-group {
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  display: flex;
  align-items: center;
  gap: 0.375rem;
  max-width: 70%;
  overflow: hidden;
}

.mot-title-icon {
  flex-shrink: 0;
  width: 1rem;
  height: 1rem;
  color: var(--mot-tb-icon);
  opacity: 1;
}

.mot-title {
  font-family: -apple-system, BlinkMacSystemFont, "Helvetica Neue", "Segoe UI", sans-serif;
  font-size: 0.8125rem;
  font-weight: 400;
  color: var(--mot-tb-title);
  letter-spacing: 0;
  text-transform: none;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.mot-dots-spacer {
  width: 3.25rem;
  z-index: 1;
}

/* ─── Terminal Body ──────────────────────────── */

.mot-body {
  padding: 1.25rem 1.25rem 1.5rem;
  font-family: var(--font-mono, "SF Mono", "Fira Code", "Cascadia Code", monospace);
  font-size: 0.8125rem;
  line-height: 1.7;
  min-height: 260px;
}

/* ─── Lines ──────────────────────────────────── */

.mot-line {
  display: flex;
  align-items: baseline;
  gap: 0.625rem;
  white-space: pre;
  min-width: 0;
}

.mot-line-blank {
  height: 0.5em;
}

.mot-line-command {
  align-items: flex-start;
}

/* ─── Prompt ─────────────────────────────────── */

.mot-prompt {
  color: var(--mot-prompt);
  font-weight: 600;
  flex-shrink: 0;
  user-select: none;
}

/* ─── Typed Command Text ─────────────────────── */

.mot-typed {
  color: var(--mot-typed);
  white-space: pre-wrap;
  overflow-wrap: anywhere;
  word-break: break-word;
  min-width: 0;
  flex: 1;
}

/* ─── Output Text ────────────────────────────── */

.mot-text {
  color: var(--mot-text);
}

.mot-text-success {
  color: var(--mot-text-success);
}

.mot-text-error {
  color: var(--mot-text-error);
}

.mot-spinner {
  color: var(--mot-text);
  display: inline-block;
  width: 1ch;
  text-align: center;
}

/* ─── Cursor ─────────────────────────────────── */

.mot-cursor {
  color: var(--mot-cursor);
  font-size: 1em;
  line-height: 1;
}

/* ─── Responsive ─────────────────────────────── */

@media (max-width: 480px) {
  .mot-body {
    padding: 1rem;
    font-size: 0.75rem;
  }

  .mot-titlebar {
    padding: 0.5rem 0.625rem;
  }

  .mot-dot {
    width: 0.625rem;
    height: 0.625rem;
  }

  .mot-title {
    font-size: 0.75rem;
  }

  .mot-title-icon {
    width: 0.875rem;
    height: 0.875rem;
  }
}

/* ─── Reduced Motion ─────────────────────────── */

@media (prefers-reduced-motion: reduce) {
  .mot-cursor {
    animation: none !important;
    opacity: 1 !important;
  }
}
