import { PricingSectionV1 } from "../pricing-section-v1"

export default function Page() {
    return <PricingSectionV1 />
}

/* ─── Pricing Section V1 ─────────────────────────── */
/* 3-tier pricing · Monthly/Annual toggle · Dark navy  */
/* Full light & dark mode support                      */
/* Fonts — heading uses --font-serif from globals.css  */

/* ─── Theme Variables (Light) ───────────────── */

:root {
  --ps1-bg: #f8fafc;
  --ps1-text: #0f172a;
  --ps1-text-muted: rgba(15, 23, 42, 0.55);
  --ps1-heading-color: #0f172a;
  --ps1-card-bg: #ffffff;
  --ps1-card-border: rgba(15, 23, 42, 0.1);
  --ps1-card-highlight-bg: #0f172a;
  --ps1-card-highlight-border: rgba(255, 255, 255, 0.12);
  --ps1-card-highlight-text: #f1f5f9;
  --ps1-card-highlight-muted: rgba(241, 245, 249, 0.6);
  --ps1-toggle-bg: rgba(15, 23, 42, 0.06);
  --ps1-toggle-active-bg: #0f172a;
  --ps1-toggle-active-text: #ffffff;
  --ps1-toggle-text: rgba(15, 23, 42, 0.6);
  --ps1-accent: #14b8a6;
  --ps1-accent-glow: rgba(20, 184, 166, 0.15);
  --ps1-badge-muted-bg: rgba(15, 23, 42, 0.08);
  --ps1-badge-muted-text: rgba(15, 23, 42, 0.7);
  --ps1-badge-accent-bg: rgba(20, 184, 166, 0.12);
  --ps1-badge-accent-text: #0d9488;
  --ps1-discount-bg: rgba(20, 184, 166, 0.12);
  --ps1-discount-text: #0d9488;
  --ps1-bullet: rgba(15, 23, 42, 0.35);
  --ps1-bullet-highlight: rgba(241, 245, 249, 0.4);
  --ps1-cta-primary-bg: var(--ps1-accent);
  --ps1-cta-primary-text: #ffffff;
  --ps1-features-heading-color: rgba(15, 23, 42, 0.8);
}

/* ─── Theme Variables (Dark) ────────────────── */

.dark {
  --ps1-bg: #060a13;
  --ps1-text: #e2e8f0;
  --ps1-text-muted: rgba(226, 232, 240, 0.5);
  --ps1-heading-color: #f1f5f9;
  --ps1-card-bg: rgba(15, 23, 42, 0.5);
  --ps1-card-border: rgba(148, 163, 184, 0.1);
  --ps1-card-highlight-bg: rgba(15, 30, 52, 0.8);
  --ps1-card-highlight-border: rgba(148, 163, 184, 0.15);
  --ps1-card-highlight-text: #f1f5f9;
  --ps1-card-highlight-muted: rgba(241, 245, 249, 0.55);
  --ps1-toggle-bg: rgba(148, 163, 184, 0.08);
  --ps1-toggle-active-bg: rgba(241, 245, 249, 0.12);
  --ps1-toggle-active-text: #f1f5f9;
  --ps1-toggle-text: rgba(148, 163, 184, 0.6);
  --ps1-accent: #2dd4bf;
  --ps1-accent-glow: rgba(45, 212, 191, 0.08);
  --ps1-badge-muted-bg: rgba(148, 163, 184, 0.1);
  --ps1-badge-muted-text: rgba(148, 163, 184, 0.8);
  --ps1-badge-accent-bg: rgba(45, 212, 191, 0.12);
  --ps1-badge-accent-text: #2dd4bf;
  --ps1-discount-bg: rgba(45, 212, 191, 0.12);
  --ps1-discount-text: #2dd4bf;
  --ps1-bullet: rgba(148, 163, 184, 0.4);
  --ps1-bullet-highlight: rgba(241, 245, 249, 0.4);
  --ps1-cta-primary-bg: var(--ps1-accent);
  --ps1-cta-primary-text: #0f172a;
  --ps1-features-heading-color: rgba(226, 232, 240, 0.75);
}

/* ─── Section Root ───────────────────────────── */

.ps1-section {
  width: 100%;
  overflow: hidden;
  background: var(--ps1-bg);
  color: var(--ps1-text);
  font-family: var(--font-sans);
}

.ps1-inner {
  position: relative;
  width: 100%;
  max-width: 76rem;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 4rem 1.25rem;
}

/* ─── Heading ───────────────────────────────── */

.ps1-heading {
  font-family: var(--font-serif);
  font-size: clamp(1.75rem, 4vw, 3rem);
  font-weight: 400;
  line-height: 1.15;
  letter-spacing: -0.02em;
  color: var(--ps1-heading-color);
  text-align: center;
  margin: 0 0 2rem;
  animation: ps1-fade-up 0.55s cubic-bezier(0.22, 1, 0.36, 1) 0.1s both;
}

/* ─── Billing Toggle ─────────────────────────── */

.ps1-toggle-wrap {
  display: inline-flex;
  align-items: center;
  gap: 0;
  background: var(--ps1-toggle-bg);
  border-radius: 9999px;
  padding: 0.25rem;
  margin-bottom: 3rem;
  animation: ps1-fade-up 0.55s cubic-bezier(0.22, 1, 0.36, 1) 0.15s both;
}

.ps1-toggle-btn {
  position: relative;
  display: inline-flex;
  align-items: center;
  gap: 0.375rem;
  padding: 0.5rem 1.125rem;
  border-radius: 9999px;
  border: none;
  background: transparent;
  color: var(--ps1-toggle-text);
  font-family: var(--font-sans);
  font-size: 0.8125rem;
  font-weight: 500;
  letter-spacing: 0.01em;
  cursor: pointer;
  transition: background 0.2s, color 0.2s;
  white-space: nowrap;
}

.ps1-toggle-btn:hover {
  color: var(--ps1-text);
}

.ps1-toggle-active {
  background: var(--ps1-toggle-active-bg);
  color: var(--ps1-toggle-active-text);
}

.ps1-toggle-active:hover {
  color: var(--ps1-toggle-active-text);
}

.ps1-discount-badge {
  display: inline-flex;
  align-items: center;
  padding: 0.125rem 0.4375rem;
  border-radius: 9999px;
  background: var(--ps1-discount-bg);
  color: var(--ps1-discount-text);
  font-size: 0.6875rem;
  font-weight: 600;
  letter-spacing: 0.01em;
  line-height: 1.4;
}

/* ─── Cards Grid ─────────────────────────────── */

.ps1-cards {
  display: grid;
  grid-template-columns: 1fr;
  gap: 1.25rem;
  width: 100%;
  max-width: 68rem;
  align-items: start;
  animation: ps1-fade-up 0.6s cubic-bezier(0.22, 1, 0.36, 1) 0.25s both;
}

/* ─── Card ───────────────────────────────────── */

.ps1-card {
  display: flex;
  flex-direction: column;
  background: var(--ps1-card-bg);
  border: 1px solid var(--ps1-card-border);
  border-radius: 0.875rem;
  padding: 2rem;
  transition: border-color 0.25s;
}

.ps1-card:hover {
  border-color: rgba(148, 163, 184, 0.2);
}

.ps1-card-highlighted {
  background: var(--ps1-card-highlight-bg);
  border-color: var(--ps1-card-highlight-border);
  color: var(--ps1-card-highlight-text);
  box-shadow: 0 0 60px -12px var(--ps1-accent-glow),
    0 0 30px -8px var(--ps1-accent-glow);
}

.ps1-card-highlighted:hover {
  border-color: rgba(148, 163, 184, 0.25);
}

.ps1-card-highlighted .ps1-plan-desc,
.ps1-card-highlighted .ps1-features-label {
  color: var(--ps1-card-highlight-muted);
}

.ps1-card-highlighted .ps1-features-heading {
  color: var(--ps1-card-highlight-text);
}

.ps1-card-highlighted .ps1-feature-item::before {
  background: var(--ps1-bullet-highlight);
}

/* ─── Card Header ────────────────────────────── */

.ps1-card-header {
  margin-bottom: 1.75rem;
}

.ps1-plan-name-row {
  display: flex;
  align-items: center;
  gap: 0.625rem;
  margin-bottom: 1rem;
}

.ps1-plan-name {
  font-size: 1.0625rem;
  font-weight: 500;
  letter-spacing: -0.01em;
  margin: 0;
}

.ps1-badge {
  display: inline-flex;
  align-items: center;
  padding: 0.1875rem 0.5rem;
  border-radius: 0.25rem;
  font-size: 0.625rem;
  font-weight: 600;
  letter-spacing: 0.06em;
  text-transform: uppercase;
  line-height: 1.4;
}

.ps1-badge-muted {
  background: var(--ps1-badge-muted-bg);
  color: var(--ps1-badge-muted-text);
}

.ps1-badge-accent {
  background: var(--ps1-badge-accent-bg);
  color: var(--ps1-badge-accent-text);
}

/* ─── Price ───────────────────────────────────── */

.ps1-price-row {
  display: flex;
  align-items: baseline;
  gap: 0.125rem;
  margin-bottom: 0.625rem;
}

.ps1-price {
  font-size: clamp(2rem, 3vw, 2.75rem);
  font-weight: 600;
  letter-spacing: -0.03em;
  line-height: 1;
}

.ps1-price-period {
  font-size: 0.8125rem;
  color: var(--ps1-text-muted);
  letter-spacing: -0.01em;
}

.ps1-card-highlighted .ps1-price-period {
  color: var(--ps1-card-highlight-muted);
}

.ps1-plan-desc {
  font-size: 0.8125rem;
  line-height: 1.5;
  color: var(--ps1-text-muted);
  margin: 0;
}

/* ─── Card Body / Features ───────────────────── */

.ps1-card-body {
  flex: 1;
  margin-bottom: 1.75rem;
}

.ps1-features-heading {
  font-size: 0.6875rem;
  font-weight: 700;
  letter-spacing: 0.08em;
  text-transform: uppercase;
  color: var(--ps1-features-heading-color);
  margin: 0 0 0.5rem;
}

.ps1-features-label {
  font-size: 0.8125rem;
  color: var(--ps1-text-muted);
  margin: 0 0 1rem;
  line-height: 1.5;
}

.ps1-feature-list {
  list-style: none;
  margin: 0;
  padding: 0;
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}

.ps1-feature-item {
  position: relative;
  padding-left: 1.125rem;
  font-size: 0.75rem;
  font-weight: 500;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  line-height: 1.5;
  color: inherit;
}

.ps1-feature-item::before {
  content: "";
  position: absolute;
  left: 0;
  top: 0.375rem;
  width: 0.375rem;
  height: 0.375rem;
  border-radius: 1px;
  background: var(--ps1-bullet);
}

/* ─── Card Footer / CTA ──────────────────────── */

.ps1-card-footer {
  margin-top: auto;
}

.ps1-cta-btn {
  width: 100%;
  border-radius: 0.5rem;
  font-weight: 600;
  letter-spacing: -0.01em;
}

.ps1-cta-primary {
  background: var(--ps1-cta-primary-bg) !important;
  color: var(--ps1-cta-primary-text) !important;
  border-color: var(--ps1-cta-primary-bg) !important;
}

.ps1-cta-primary:hover {
  opacity: 0.9;
}

/* ─── Animations ─────────────────────────────── */

@keyframes ps1-fade-up {
  from {
    opacity: 0;
    transform: translateY(18px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* ─── Responsive ─────────────────────────────── */

@media (min-width: 640px) {
  .ps1-inner {
    padding: 5rem 1.5rem;
  }

  .ps1-cards {
    gap: 1.5rem;
  }
}

@media (min-width: 768px) {
  .ps1-inner {
    padding: 5rem 2rem;
  }

  .ps1-cards {
    grid-template-columns: repeat(3, 1fr);
    gap: 1.25rem;
  }

  .ps1-card-highlighted {
    transform: scale(1.02);
  }
}

@media (min-width: 1024px) {
  .ps1-inner {
    padding: 6rem 2.5rem;
  }

  .ps1-cards {
    gap: 1.5rem;
  }

  .ps1-card {
    padding: 2.25rem;
  }
}

@media (min-width: 1280px) {
  .ps1-inner {
    padding: 6rem 3rem;
  }

  .ps1-card {
    padding: 2.5rem;
  }
}

/* ─── Reduced Motion ─────────────────────────── */

@media (prefers-reduced-motion: reduce) {
  .ps1-heading,
  .ps1-toggle-wrap,
  .ps1-cards {
    animation: none;
  }
}
