"use client";

import { useEffect, useRef, useState } from "react";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import { Button } from "@/components/ui/button";
import { RotateCw } from "lucide-react";

// Register GSAP plugins
gsap.registerPlugin(ScrollTrigger);

interface SplitTextAnimationProps {
  text: string;
  animationType?: "fade" | "slide" | "scale" | "rotate";
  direction?: "up" | "down" | "left" | "right";
  duration?: number;
  stagger?: number;
  delay?: number;
  className?: string;
  showRerunButton?: boolean;
}

export function SplitTextAnimation({
  text,
  animationType = "fade",
  direction = "up",
  duration = 0.8,
  stagger = 0.05,
  delay = 0,
  className = "",
  showRerunButton = false,
}: SplitTextAnimationProps) {
  const containerRef = useRef<HTMLDivElement>(null);
  const charsRef = useRef<HTMLSpanElement[]>([]);
  const [rerunKey, setRerunKey] = useState(0);

  useEffect(() => {
    if (!containerRef.current) return;

    // Clear container first
    containerRef.current.innerHTML = "";

    // Split text into characters
    const words = text.split(" ");
    const chars: HTMLSpanElement[] = [];

    words.forEach((word) => {
      const wordSpan = document.createElement("span");
      wordSpan.style.display = "inline-block";
      wordSpan.style.marginRight = "0.25em";

      word.split("").forEach((char) => {
        const charSpan = document.createElement("span");
        charSpan.textContent = char;
        charSpan.style.display = "inline-block";
        charSpan.style.opacity = "0";
        wordSpan.appendChild(charSpan);
        chars.push(charSpan);
      });

      containerRef.current?.appendChild(wordSpan);
    });

    charsRef.current = chars;

    // Set initial state based on animation type
    const setInitialState = () => {
      chars.forEach((char) => {
        gsap.set(char, { opacity: 0 });

        switch (animationType) {
          case "fade":
            gsap.set(char, { opacity: 0 });
            break;
          case "slide":
            const slideDistance = 50;
            gsap.set(char, {
              opacity: 0,
              y:
                direction === "up"
                  ? slideDistance
                  : direction === "down"
                  ? -slideDistance
                  : 0,
              x:
                direction === "left"
                  ? slideDistance
                  : direction === "right"
                  ? -slideDistance
                  : 0,
            });
            break;
          case "scale":
            gsap.set(char, { opacity: 0, scale: 0 });
            break;
          case "rotate":
            gsap.set(char, { opacity: 0, rotation: 180 });
            break;
        }
      });
    };

    setInitialState();

    // Create animation timeline
    const tl = gsap.timeline({
      delay,
      scrollTrigger: {
        trigger: containerRef.current,
        start: "top 80%",
        toggleActions: "play none none none",
      },
    });

    // Animate characters
    switch (animationType) {
      case "fade":
        tl.to(chars, {
          opacity: 1,
          duration,
          stagger,
          ease: "power2.out",
        });
        break;
      case "slide":
        tl.to(chars, {
          opacity: 1,
          y: 0,
          x: 0,
          duration,
          stagger,
          ease: "power2.out",
        });
        break;
      case "scale":
        tl.to(chars, {
          opacity: 1,
          scale: 1,
          duration,
          stagger,
          ease: "back.out(1.7)",
        });
        break;
      case "rotate":
        tl.to(chars, {
          opacity: 1,
          rotation: 0,
          duration,
          stagger,
          ease: "back.out(1.7)",
        });
        break;
    }

    return () => {
      // Cleanup
      tl.kill();
      ScrollTrigger.getAll().forEach((trigger) => trigger.kill());
    };
  }, [text, animationType, direction, duration, stagger, delay, rerunKey]);

  const handleRerun = () => {
    setRerunKey((prev) => prev + 1);
  };

  return (
    <>
      <div
        ref={containerRef}
        className={`text-2xl md:text-3xl font-bold tracking-tight ${className}`}
        style={{ lineHeight: "1.2" }}
      />
      {showRerunButton && (
        <Button
          variant="outline"
          size="sm"
          onClick={handleRerun}
          className="absolute top-4 right-4 gap-2"
        >
          <RotateCw className="h-4 w-4" />
          Rerun
        </Button>
      )}
    </>
  );
}

"use client";

import { SplitTextAnimation } from "./split-text-animation";

export function SplitTextDemo() {
  return (
    <div className="flex flex-col gap-16 items-center justify-center min-h-[600px] p-8">
      <div className="flex flex-col gap-8 w-full max-w-4xl">
        {/* Fade Animation */}
        <div className="flex flex-col gap-4">
          <h3 className="text-lg font-medium text-muted-foreground">Fade In</h3>
          <SplitTextAnimation
            text="Build Beautiful Interfaces"
            animationType="fade"
            duration={0.8}
            stagger={0.03}
          />
        </div>

        {/* Slide Up Animation */}
        <div className="flex flex-col gap-4">
          <h3 className="text-lg font-medium text-muted-foreground">
            Slide Up
          </h3>
          <SplitTextAnimation
            text="With Glsilk Components"
            animationType="slide"
            direction="up"
            duration={0.8}
            stagger={0.03}
          />
        </div>

        {/* Scale Animation */}
        <div className="flex flex-col gap-4">
          <h3 className="text-lg font-medium text-muted-foreground">
            Scale In
          </h3>
          <SplitTextAnimation
            text="Create Amazing Experiences"
            animationType="scale"
            duration={0.6}
            stagger={0.04}
          />
        </div>

        {/* Rotate Animation */}
        <div className="flex flex-col gap-4">
          <h3 className="text-lg font-medium text-muted-foreground">
            Rotate In
          </h3>
          <SplitTextAnimation
            text="Transform Your Ideas"
            animationType="rotate"
            duration={0.7}
            stagger={0.04}
          />
        </div>

        {/* Slide Left Animation */}
        <div className="flex flex-col gap-4">
          <h3 className="text-lg font-medium text-muted-foreground">
            Slide Left
          </h3>
          <SplitTextAnimation
            text="Into Reality"
            animationType="slide"
            direction="left"
            duration={0.8}
            stagger={0.03}
          />
        </div>
      </div>
    </div>
  );
}
